css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

*:not([style*="rgb"]){
-moz-appearance: -moz-win-glass !important ;
background-color: transparent !important;
color:white !important;
text-shadow: black 0px -2px 1px,
black 0px 2px 1px,
black -1px 0px 1px,
black 1px 0px 1px !important;	
}

notification[type="critical"]
{
background:rgba(119,34,34,0.7) !important;
}

.alertBox 
{
background:black !important;
}

thumb:active, thumb:hover, scrollbarbutton:hover
{
background: rgba(0,0,0,0.5) !important;
}

scrollbar
{
-moz-appearance: none!important;
background:transparent !important;
min-height: 16px !important;
min-width: 16px !important
}

scrollbarbutton, scrollbar thumb 
{
border:solid 1px !important;
-moz-border-radius: 15px !important;
}

statusbarpanel, #status-bar, .tabs-bottom , #navigator-toolbox, groupbox, tabpanels, 
#PopupAutoCompleteRichResult, richlistbox, .sidebar-placesTree, #identity-popup-container,
toolbar, toolbarseparator , .tabs-right, #cookieInfoBox, .paneSelector , .viewSelector
{
border: none !important;
}

.tabbrowser-tabs tab,.searchbar-engine-button
{
-moz-border-radius: 16px !important;
}

#showWhenDownloading, #closeWhenDone, #saveWhere, #alwaysAsk, #useCursorNavigation, 
#searchStartTyping, #blockAutoRefresh, #useAutoScroll, #useSmoothScrolling, #checkSpelling
{
border: none !important;
margin: -2px 0 -2px 0 !important;
opacity:0.99 !important
}

.groupbox-title
{
background-color: rgba(0,0,0,.6) !important;
border: solid 1px rgba(0,0,0,0.9) !important;
font-size:12pt !important;
-moz-border-radius: 15px !important;
opacity:0.99 !important;
padding:0 0 3px 3px !important;
}

#PopupAutoCompleteRichResult > richlistbox > scrollbox
{
margin: 2px 15px 15px 0 !important;
}

#PopupAutoCompleteRichResult > richlistbox > scrollbox > box
{
border: 0 !important;
padding: 0 10px 0 5px !important;
}

richlistitem[selected=true]
{
border: solid grey 1px!important;
-moz-border-radius: 6px !important;
background: rgba(255,255,255,0.1) !important; 
}

#toolbar-menubar
{
margin-top:1px!important;
}

.tabs-container
{
margin-top:2px!important;
margin-bottom:-4px!important;
}

.tabs-alltabs-button, .tabs-newtab-button, .tabbrowser-tab
{
margin: 1px 0 2px 0!important;
padding: 0 !important;
}

tab[selected="true"]
{
margin:0 0 5px 0!important;
color: white !important;
background-color: rgba(0,255,255,.1) !important;
border-bottom: 1px solid rgba(0,255,255,0.99) !important;
-moz-border-right-colors:rgba(0,255,255,0.5) !important;
opacity:1!important
}

tab[selected="true"]:hover
{
background-color: rgba(0,255,255,.2) !important;
-moz-border-top-colors:rgba(0,255,255,0.3) !important;
-moz-border-left-colors:rgba(0,255,255,0.3) !important;
}

.tabbrowser-tabs tab:not([selected="true"]):hover
{
background-color: rgba(0,100,155,.35) !important;
border-bottom: 1px solid rgba(0,100,155,0.99) !important;
border-right: 1px solid rgba(0,100,155,0.99) !important;
-moz-border-top-colors:rgba(0,100,155,0.5) !important;
-moz-border-left-colors:rgba(0,100,155,0.5) !important;
-moz-border-right-colors:rgba(0,100,2155,0.99) !important;
opacity:1!important
}

.tabbrowser-tabs tab:not([selected="true"])
{
margin:5px 1px 5px 1px !important;
background-color: rgba(0,100,155,.25) !important;
-moz-border-right-colors:rgba(0,100,2155,0.5) !important;
opacity: 0.7 !important;
}

button:not(#feed-button):not(.searchbar-engine-button),
input[type="submit"] , [type="reset"] 
{
-moz-appearance: none !important;		
border: 2px solid rgba(0,0,0,0.9) !important;
-moz-border-radius: 12px!important;
background: url(chrome://browser/skin/tabbrowser/tab-bkgnd.png) !important; 
padding: 2px 5px 2px 5px!important;
}

button:not(#feed-button):not(.searchbar-engine-button):hover,
input[type="submit"]:hover , [type="reset"]:hover 
{
background: url(chrome://browser/skin/tabbrowser/tab-hover-bkgnd.png) !important; 
}

button *
{
border: none !important; 
}

dropmarker:hover , .tabs-bottom, .tabbrowser-strip, .tabbrowser-tabs 
{
background: none!important;
border-left:dashed 1px rgba(0,0,0,0) !important;
}

#urlbar-container 
{
margin-left: -5px !important;
}

#urlbar:hover, .searchbar-textbox:hover
{
background: url(chrome://browser/skin/tabbrowser/tab-hover-bkgnd.png) !important; 
}

#urlbar
{
border-bottom: 1px solid rgba(0,255,255,0.99) !important;
border-top:solid 1px rgba(0,255,255,0.99) !important;
border-left:solid 1px rgba(0,255,255,0.99) !important;
border-right:solid 1px rgba(0,255,255,0.99) !important;
margin: 1px 5px 2px 45px!important;
-moz-border-radius: 16px !important;
min-height: 24px !important;
max-height: 24px !important;
background: url(chrome://browser/skin/tabbrowser/tab-bkgnd.png) !important; 
}

.searchbar-textbox 
{
border-bottom: 1px solid rgba(0,255,255,0.99) !important;
border-top:solid 1px rgba(0,255,255,0.99) !important;
border-left:solid 1px rgba(0,255,255,0.99) !important;
border-right:solid 1px rgba(0,255,255,0.99) !important;
padding: 0px 10px 0px 10px!important;
margin: 0px 5px 5px 5px!important;
-moz-border-radius: 16px !important;
min-height: 24px !important;
max-height: 24px !important;
background: url(chrome://browser/skin/tabbrowser/tab-bkgnd.png) !important; 
}

.search-go-button 
{
padding: 0px !important;
margin-right:-8px!important;
margin-bottom: -1px !important;
}

.searchbar-engine-button
{
margin-left: -11px !important;  
width: 19px !important;
height: 22px !important;
border-right: none !important;
margin-top: -1px !important;
margin-bottom: -1px !important;
}

.popup-internal-box
{
opacity:0.99 !important;
padding: 5px 15px 8px 15px !important;
}

menupopup[menugenerated="true"],menupopup,popup,#editBookmarkPanel, #PopupAutoComplete
{
padding: 0px 15px 15px 0px !important;
border: none !important;
}

.menuitem-iconic.bookmark-item
{
margin-left:0px !important;
}

prefpane
{
padding: 5px !important;
}

#historyGroup
{
margin: 0px 0px -65px 0px !important;
}

#historyRememberPane
{
padding: 0px 0px 55px 0px !important;
}

#viewGroup radio, radio[pane]{
width:75px!important;
border:solid 1px rgba(0,0,0,0)!important;
font-weight:bolder!important;
margin:8px 2px 8px 2px!important;
}

radio[pane][selected="true"], radio[pane]:hover, 
#viewGroup radio[selected="true"], #viewGroup radio:hover
{ 
background: url(chrome://browser/skin/tabbrowser/tab-hover-bkgnd.png) !important; 
background-color: rgba(255,255,255,0.12) !important;
border: solid 1px rgba(0,255,255,0.5) !important;
-moz-box-shadow: 0 1px 9px 1px rgba(255,255,255,0.5) !important;
-moz-border-radius: 12px;
}

radio[pane][selected="true"]:hover, #viewGroup radio[selected="true"]:hover
{
border: solid 1px rgba(0,255,255,0.8) !important;
-moz-box-shadow: 0 1px 9px 1px rgba(255,255,255,0.99) !important;
}

tabbox tabs tab[selected="true"]:not(.tabbrowser-tab), tabbox tabs tab:not(.tabbrowser-tab):hover
{ 
background-color: rgba(255,255,255,0.08) !important;
background-image: url(chrome://browser/skin/tabbrowser/tab-hover-bkgnd.png) !important; 
border: solid 1px rgba(0,255,255,0.3) !important;
-moz-box-shadow: 0 1px 9px 1px rgba(255,255,255,0.5) !important;
margin:-5px 0 0 0!important;
}

tabbox tabs tab[selected="true"]:not(.tabbrowser-tab):hover
{
background-color: rgba(255,255,255,0.08) !important;
border: solid 1px rgba(0,255,255,0.8) !important;
background-image: url(chrome://browser/skin/tabbrowser/tab-hover-bkgnd.png) !important; 
-moz-box-shadow: 0 1px 9px 1px rgba(255,255,255,0.99) !important;
}

tabbox tabs tab:not(.tabbrowser-tab)
{
padding-top:3px !important;
font-weight:bolder!important;
background-color: rgba(255,255,255,0.08) !important;
background-image: url(chrome://browser/skin/tabbrowser/tab-bkgnd.png) !important; 
border: solid 1px rgba(0,255,255,0.3) !important;
-moz-border-radius-topleft: 12px!important;
-moz-border-radius-topright: 12px!important;
}

.tab-middle.box-inherit[selected="true"]
{
outline:none!important;
}

#tabsElement, #certMgrTabbox
{
padding: 12px 0 0 18px !important;
margin-bottom:-8px !important;
}

tab
{
border: solid 1px rgba(0,100,155,1)!important;
}

#miscGroup .groupbox-title, #addonsPhishingGroup .groupbox-title
{
display: none !important;
}

#handlersView, #itemList, #cookieInfoGrid, #rejectsTree, #signonsTree, 
#tabPrefsBox, #miscGroup .groupbox-body, #addonsPhishingGroup .groupbox-body,
#offlineAppsList, #generalPanel,#networkPanel,#updatePanel, #encryptionPanel, 
#othersCerts, #myCerts, #webCerts, #CACerts, #OrphanCerts, #crltree, #info_tree,
#device_tree
{
background-color: rgba(0,0,0,.25) !important;
Border:solid 1px !important;
-moz-border-bottom-colors: threedhighlight threedlightshadow!important;
-moz-border-top-colors: threedshadow threedshadow!important;
-moz-border-radius: 12px;
padding: 3px !important;
}

#tabPrefsBox
{
padding: 40px !important;
margin:30px!important;
}

textbox:not(#urlbar):not(.searchbar-textbox),menulist,#permList
{
background-color: rgba(0,0,0,.25) !important;
-moz-border-radius: 12px;
padding: 2px 0px 3px 8px !important;
}

#imagetree, .inset.iframe,.tree-stack, #permissionsTree, #CookiesDialog tree, 
#user-tree, #orphan-tree, #ca-tree, #server-tree, #email-tree, #tabList, #engineList
{
-moz-border-radius: 12px;
padding: 3px 3px 3px 3px !important;
}

#metatree, #activeLanguages, #rejectCol, #deviceCol
{
-moz-border-radius: 12px;
}

#metaTags > box{
margin-bottom:-12px!important;
}

#securityBox > hbox{margin:5px 0 -5px 0!important;}

#securityBox > box{padding:12px 0 0 0!important;}

#image-address , #meta-name , #siteCol, #typeColumn, #domainCol, #certcol,
#Col1, #title1Col, #restore, #engineName
{
-moz-border-radius: 12px 0 0 12px !important;
}

.treecol-image, #meta-content, #statusCol, #actionColumn, #nameCol, #userCol,
#title2Col, #title, treecol[label="Windows and Tabs"], #engineKeyword
{
-moz-border-radius: 0 12px 12px 0 !important;
}

.menulist-dropmarker
{
background-color: rgba(0,0,0,.25) !important;
-moz-border-radius: 12px;
margin: 0px 2px 0px 0px !important;
}

#mediaSplitter
{
margin:5px 0 5px 0!important;
padding: 0px !important;
border-bottom: none !important;
}

menuseparator
{
-moz-box-shadow: 0 0 2px 1px !important;
padding: 0px !important;
border: none !important;
}

autorepeatbutton 
{
-moz-box-shadow: 0 0 3px #c0c0c0;
-moz-border-radius: 15px !important;
height: 14px !important;
border: solid 1px #c0c0c0 !important;

}

menuitem[disabled="true"]>label
{
color: grey !important;
text-shadow: #000 3px 3px 2px!important;
opacity: 0.6 !important;
}

menuitem,menu
{
padding-top: 4px!important;
padding-bottom: 5px!important;
}

menubar > menu 
{
border-left: none !important;
border-right: none !important;
margin-top: -2px!important;
margin-bottom: -1px!important;
}

menupopup menu:hover, popup menu:hover,treechildren:-moz-tree-row(hover),menu:hover,
menuitem:hover, richlistitem:hover , richlistitem[selected=true]:hover,
toolbarbutton:not([id*="back"]):not([id*="forward"]):not(.tab-close-button),
#PopupAutoComplete > tree > treechildren:-moz-tree-row(selected)
{
-moz-appearance: toolbarbutton !important;
background: transparent !important;
}

*:focus
{
border:none !important;
outline:none!important;
}

#btTooltip,#aHTMLTooltip,#urlTooltip,#un-toolbar-tooltip,#tooltip,.tooltip,tooltip,
#brief-tooltip,#brief-tooltip-last-updated,#brief-tooltip-no-unread,#brief-tooltip-feed-name,
#brief-tooltip-unread-count,#brief-tooltip-unread-desc,#brief-tooltip-rows,#showcaseTooltipLeftArrow,
#showcaseTooltipRightArrow,#linkReporterTitleTooltip,#linkReporterURLTooltip,#linkReporterURLIconTooltip
{
border: none !important;
padding: 5px 15px 15px 5px !important; 
}

#urlTooltip,#identity-popup
{
border: none !important;
padding: 5px 20px 20px 0px !important; 
}

#btTitleText
{ 
font-size: 14px !important;
margin-top: -2px !important; 
} 

#btUrlText 
{ 
color: white !important;
font-size: 12px !important;
margin: -5px 0 3px 0 !important; 
opacity: 0.65 !important; 
}

#identity-box,
#identity-box.verifiedIdentity,
#identity-box.verifiedDomain,
#identity-box.unknownIdentity
{
border: 0 !important;
margin: -1px !important;
padding: 1px 1px 1px 4px !important;
-moz-border-radius: 13px 0 0 13px !important;
}

treechildren, label, #Console2Window, #JSConsoleWindow, dialog, 
#BrowserPreferences, [id*="Dialog"], #places, richlistitem, #mainDeck
{
opacity:0.99 !important;
}

treechildren:-moz-tree-cell-text
{
color:steelblue !important;
}

treechildren:-moz-tree-cell-text(leaf)
{
color:white!important;
}

treechildren:-moz-tree-row(even)
{
background: rgba(0,0,0,0.55)!important;
}

treechildren:-moz-tree-row(odd)
{
background: rgba(0,0,0,0.45)!important;
}

#placesView > splitter,#sidebar-splitter
{
border-right:none!important;
}

textbox:not([disabled]) , menulist, #permList , 
#activeLanguages, #metatree, #imagetree, 
#permissionsTree, #cookiesList, #engineList
{
border: 1px inset !important;
}

#user-tree, #orphan-tree, #ca-tree, #server-tree, #email-tree
{
margin-bottom:10px !important; 
border: 1px inset !important;
}

.colorpicker-button-menupopup
{
border: none !important;
padding-bottom: 15px !important;
padding-right: 15px !important;
}

@-moz-document
 url-prefix(http://)
 {}
