css:
@-moz-document url-prefix("https://www.fakemail.net") {
.main .dynamic {
    border: 1px solid #00dcff;
    flex-grow: 1;
}


.main .navigace ul li a {
    border-bottom: 1px solid #00d0ff;
}

.info div .links:not(.barevne) a.color {
    font-weight: bold;
    color: #16aee8;
}

.main .navigace ul li.active a {
    color: #fff;
    background: #58b2ff;
}

a:hover {
    color: #064ae4;
}


.info div div:not(.links):not(.avatarDiv) {
    box-sizing: border-box;
    border: 1px solid #005fb1;
    height: 50px;
}

body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    font-size: 14px;
    line-height: 1.42857143;
    color: #16aee8;
}


.info div.tbg div:not(.links) {
    background-color: rgba(14, 156, 255, .25);
    display: flex;
    flex-direction: row;
    justify-content: center;
    padding: 0px 5px;
    align-items: center;
}


.info {
    margin-bottom: 0px !important;
    padding: 0px;
    flex-direction: row;
    align-items: flex-start;
    background-color: #d9e9fd00;
}

.style {
    color: #16aee8;
    font-size: 34px;
    background-color:#58b2ff;
}
 
#odesilatel {
    color: #1000ff;
}

    
a.color.cetc {
    color: #16aee8

}

.klikaciRadek.newMail .from .glyphicon {
    color: #08ff00;
}

a, a:visited, a:link, a:active {
    color: #0095ff;
}

.info div .links p {
       color: #002af7;
}

.info div .links p span {
    color: #ff3210;
}

.zpetMain, .zavriOkno, #oknoLink {
  
    color: #14ce2a;
}

div.col-xs-0.col-md-2.hidden-mobile div[style] {
    background-color: rgba(14, 156, 255, .25) !important;
    
}

a.colo.cetc div[style] {
    color: #16aee8;
}


    
}
