css:
@-moz-document url-prefix("https://www.edgegamers.com/") {
/*
	Author: 4g0tt3nSou1 (@4g0tt3nSou1#1337)
	Version: 1.0
*/
 .p-header {
    /*color: #bcdef5;*/
    background: linear-gradient(to right, #240059, #4f1886)
}
a {
    color: #739dee;
    background-color:inherit !important;
    text-decoration: none;
}
.avatar {
    background-color:#201e1e !important;
}
.notice.notice--primary {
	color: #D00011;
	background: #000;
    text-align:center;
}
.p-sectionLinks {
    font-size: 13px;
    color: #621ca8;
    background-color:#201e1e;
}
.fr-box.fr-basic .fr-element {
    color: #fff;
}
.p-body {
    color:#8634ca;
    background-color:#201e1e;
}
.p-body-header {
    color:#8634ca;
    background-color:#201e1e;
}
.menu-content {
    color:#8634ca;
    background-color:#201e1e;
    background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.menu--account {
    /* THIS */
    color:#8634ca;
    background-color:#201e1e;
    background: initial !important;
}
.memberHeader-main {
    background: #201e1e;
    border-bottom: 1px solid #dfdfdf;
    border-radius: 3px 3px 0 0;
}
.menu-linkRow {
    display: block;
    padding: 6px 15px 6px 12px;
    border-left: 3px solid transparent;
    color: #8634ca;
    text-decoration: none;
}
.menu-row.menu-row--alt {
    color: #8634ca;
    background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.button.button--link, a.button.button--link {
    background: #201e1e;
    color: #2577b1;
    border-color: #e5e5e5 #cbcbcb #cbcbcb #e5e5e5;
}
.message-responseRow {
    margin-top: -1px;
    background: #201e1e;
    border: 1px solid #dfdfdf;
    padding: 6px;
}
.menu-header {
    color:#8634ca;
    background-color:#201e1e;
    background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.menu-footer {
    color:#8634ca;
    background-color:#201e1e;
    background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.menu-row.menu-row--highlighted {
    color:#8634ca;
    background-color:#201e1e;
    background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.p-body-main {
    color:#8634ca;
    background-color:#312f2f;
}
.p-body-content {
    color:#8634ca;
    background-color:#201e1e;
}
.block-header {
    color:#8634ca;
    padding: 6px 10px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 20px;
    border-bottom: 1px solid #dfdfdf;
    background: initial !important;
    /*background-color:#312f2f !important;*/
    background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.block-filterBar {
	padding: 6px 10px;
	font-size: 13px;
    color:#8634ca;
	border-bottom: 1px solid #cbcbcb;
	background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.inputGroup.inputGroup--joined .inputGroup-text {
	border-width: 1px;
	border-style: solid;
	border-top-color: #cbcbcb;
	border-right-color: #dfdfdf;
	border-bottom-color: #dfdfdf;
	border-left-color: #cbcbcb;
    background: #201e1e;
	text-align: center;
	padding: 3px 6px;
}
.block-minorHeader {
    color:#8634ca;
    background-color:#312f2f;
}
.block-body {
    color:#8634ca;
    background-color:#312f2f;
}
.block-container {
    color: #8634ca;
    background: #312f2f;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    border-radius: 4px;
}
.block-row.block-row--separated {
    padding-top: 12px;
    padding-bottom: 12px;
    color: #8634ca;
    background: #201e1e;
}
.block-row.block-row--alt {
    color: #8634ca;
    background: #312f2f;
}
.block--messages .message, .block--messages .block-row {
    color: #8634ca;
    background: #201e1e;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    border-radius: 4px;
}
.fr-toolbar {
    color: #8634ca;
    background: #312f2f;
    position: relative;
    z-index: 4;
    font-family:'Segoe UI', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    padding: 0 1px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    text-align: left;
    border: 1px solid #cbcbcb;
    border-top: 3px solid #47a7eb;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 1.2;
}
.fr-box.fr-basic .fr-wrapper {
    color:#8634ca;
    background: #312f2f;
    border: 1px solid #cbcbcb;
    border-top: 0;
    top: 0;
    left: 0;
}
.tooltip--bookmark .tooltip-content, .tooltip--member .tooltip-content, .tooltip--share .tooltip-content {
    color: #141414;
    background: #312f2f;
    padding: 0;
    text-align: left;
    border: 1px solid #d8d8d8;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.35);
}
.userBanner.userBanner--staff, .userBanner.userBanner--primary {
    color: #2577b1;
    background: #201e1e;
    border-color: #bfdff8;
    border-color: #bcdef5;
}
.reactionsBar {
    display: none;
    opacity: 0;
    -webkit-transition: all .25s ease, -xf-opacity .25s ease;
    transition: all .25s ease, -xf-opacity .25s ease;
    overflow-y: hidden;
    height: 0;
    -webkit-transition-property: all, -xf-height;
    transition-property: all, -xf-height;
    background: #201e1e;
    border: 1px solid #dfdfdf;
    border-left: 2px solid #47a7eb;
    padding: 6px;
    font-size: 12px;
    margin-top: 6px;
}
.overlay-title {
    display: none;
    margin: 0;
    font-weight: 400;
    font-size: 24px;
    color: #2577b1;
    background: #edf6fd;
    border-bottom: 1px solid #dfdfdf;
    padding-top: 6px;
    padding-right: 10px;
    padding-bottom: 6px;
    padding-left: 10px;
    background: linear-gradient(0deg, #312f2f, #312f2f);
}
.menu {
    display: none;
    opacity: 0;
    -webkit-transition: all .25s ease, -xf-opacity .25s ease;
    transition: all .25s ease, -xf-opacity .25s ease;
    position: absolute;
    z-index: 200;
    margin: 8px 0 0;
    min-width: 240px;
    max-width: 320px;
    border-radius: 3px;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.35);
    background: linear-gradient(to right, #201e1e, #312f2f)!important;
}
.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 6px;
    background: #312f2f;
    border-bottom: 1px solid #dfdfdf;
    color:#8634ca;
}
.memberTooltip-stats dl.pairs.pairs--rows > dt {
    font-size: 12px;
    color: #8634ca;
}
.pairs.pairs--rows.pairs--rows--centered > dt, .pairs.pairs--rows.pairs--rows--centered > dd {
    text-align: center;
    color: #8634ca;
}
.message-gradient {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 60px;
    background: #fefefe;
    background-color:inherit !important;
}
.pageNav-jump {
    display: inline-block;
    background: linear-gradient(0deg, #312f2f, #312f2f);
    color: #2577b1;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 5px 8px;
}
.pageNav-page {
    display: table-cell;
    background: linear-gradient(0deg, #312f2f, #312f2f);
    color: #2577b1;
    border-width: 1px;
    border-style: solid;
    border-top-color: #dfdfdf;
    border-right-color: #d8d8d8;
    border-bottom-color: #cbcbcb;
    border-left-color: #d8d8d8;
    font-size: 13px;
    white-space: nowrap;
}
.pageNav-page.pageNav-page--current {
    background: #312f2f;
    color: #aa6709;
    border: 1px solid #f9c479;
    cursor: pointer;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    background: #312f2f;
    border-right: 1px solid #d8d8d8;
    min-width: 0;
}
.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 13px;
    color: #8634ca;
    background: #312f2f;
}
.bbCodeBlock {
    margin: .75em 0;
    background: #312f2f;
    border: 1px solid #e7e7e7;
    border-left: 3px solid #f2930d;
}
.formRow > dt {
    border-right: 1px solid transparent;
    background: #201e1e;
    border-color: #dfdfdf;
    text-align: right;
    width: 33%;
    padding: 15px 10px 15px 10px;
}
.formSubmitRow-bar {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: #201e1e;
    border-top: 1px solid #cbcbcb;
}
.block-footer {
    color:#8634ca;
    background: initial !important;
    background-color:#201e1e !important;
}
.p-footer {
    font-size: 13px;
    color: #000;
    background: #201e1e;
}
.u-muted {
    color: #8c8c8c;
}
.pairs > dt {
    padding: 0;
    margin: 0;
    color: #8c8c8c;
}
.p-nav-list .p-navEl.is-selected {
    color: #1988ea;
    font-weight: 700;
    background: #a206f045;
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
}
.p-nav-list .p-navEl {
    color: #1988ea;
}
.p-nav {
    color: #1988ea;
}
.label.label--primary {
    color: #1988ea;
    background: #201e1e;
    border-color: #bfdff8;
    border-color: #bcdef5;
}
.input {
    font-size: 15px;
    color: #141414;
    background: #312f2f;
    border-width: 1px;
    border-style: solid;
    border-top-color: #cbcbcb;
    border-right-color: #dfdfdf;
    border-bottom-color: #dfdfdf;
    border-left-color: #cbcbcb;
    border-radius: 4px;
    padding: 6px;
    display: block;
    width: 100%;
    line-height: 1.4;
    text-align: left;
    word-wrap: break-word;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}
.memberOverviewBlock-seeMore {
    background: #312f2f;
    border: 1px solid #dfdfdf;
    padding: 3px;
    text-align: center;
    margin-top: auto;
}
.blockLink.is-selected {
    font-weight: 700;
    background: #312f2f;
    border-left: 3px solid #47a7eb;
    padding-left: 7px;
}
}
