css:
@-moz-document domain("docs.opencv.org") {
:root
{
    --main-bg-color: #21262b;
	--main-fg-color: #bbb;
	--lighter-bg-color: #31363b;
    --lighter-fg-color: #ccc;
	--highlight-bg-color: #41464b;
    --highlight-fg-color: #ddd;
    --primary-color: #8Da7dC;
    --secondary-color: #c07070;
}

body
{
    background: var(--main-bg-color);
    color: var(--main-fg-color);
}

a, a.code, a.code:visited, a.line, a.line:visited
{
    color: var(--primary-color);
}

.paramname
{
    color: var(--secondary-color);
}

/* --- TOP --- */

/* Google Bar */
.gsc-control-cse
{
    display: none;
}

/* Title Bar */
#titlearea
{
    border-color: var(--highlight-fg-color);
}

/* Main Nav */
.sm-dox
{
    background: var(--lighter-bg-color);
}

.sm-dox a, .sm-dox a:focus, .sm-dox a:active, .sm-dox a:hover, .sm-dox a.highlighted
{
    background: var(--lighter-bg-color);
    color: var(--lighter-fg-color);
    text-shadow: none;
}

.sm-dox a:hover
{
    background: var(--highlight-bg-color);
}

.sm-dox ul
{
    background: var(--highlight-bg-color);
}

.sm-dox ul a, .sm-dox ul a:focus, .sm-dox ul a:hover, .sm-dox ul a:active
{
    background: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
}

/* Nav Path */
.navpath ul
{
    background: var(--lighter-bg-color);
    color: var(--lighter-fg-color);
    border-color: var(--lighter-fg-color);
    border-width: 1px 0px;
}

.navpath li
{
    color: var(--lighter-fg-color);
}

.navpath li.navelem a
{
    color: var(--lighter-fg-color);
    text-shadow: none;
}

/* --- Header --- */

div.header
{
    background: var(--main-bg-color);
    color: var(--main-fg-color);
    border-color: var(--lighter-fg-color);
}

/* Contents */

div.contents
{
    background: var(--main-bg-color);
    color: var(--main-fg-color);
}

code
{
    background: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
}

/* Dynamic Content */

div.center img
{
    filter: invert(1) hue-rotate(180deg);
}

/* Member Declarations */
.mdescLeft, .mdescRight, .memItemLeft, .memItemRight, .memTemplItemLeft, .memTemplItemRight, .memTemplParams
{
    background: var(--lighter-bg-color);
    color: var(--lighter-fg-color);
}

.memtitle
{
    background: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
}

.memproto, dl.reflist dt
{
    background: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
    text-shadow: none;
}

.memdoc, dl.reflist dd
{
    background: var(--lighter-bg-color);
    color: var(--lighter-fg-color);
}

div.fragment
{
    background: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
}
}
