css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com"){

	*::-moz-selection{background:#333 !important;color:#fff !important;}

    *{ color: #333 !important; font-family: "Trebuchet MS","Segoe UI","Arial","Serif" !important; }

    a{outline:none !important;}

    a,a:link,a:visited{ color: #1e529d !important; text-decoration: none !important; }

    a:hover{ color: #111 !important; }

    a:active{ color: #f4a460 !important; text-decoration: none !important; }

    .cluster{ border-bottom: 1px dotted #708090 !important; /*Separates each posts*/ padding-bottom: 1em !important; }

    .friend{ background-color: #ffffe0 !important; /*Highlight your friends comments*/ color: #111 !important; }

    .info,.likes,.comment,#footer,.pager,.bottom,.hidetoggle, #sidebar .box .box-body { font-size: 12px !important; }

    .lbody { font-style: italic !important; }

    .likes { background-color: #FFEFE8 !important; }

    .main ul li a, .section ul li a { font-size: 11px !important; }

    .owner{ background-color: #e1f0ff !important; /*Highlight your own comments*/ color: #111 !important; }

    .searchbold { background-color: #CCFFE6 !important; }

    .sharebox, .entry, .pager.top { border-bottom: 1px dotted #bbb !important; margin-bottom: 25px !important; padding-bottom: 25px !important; }

    .subscribebar, .subscribebar a:link, .subscribebar a:visited, .control, .control a:link,.control a:visited { color: #fff !important; }

    .updated{ color: #ccc !important; font-size: 11px !important; padding: 1px 0 !important; }

    #body .box-body { background-color: #fff !important; }

    #footer a,#footer a:visited,.feed .entry .info a,.feed .entry .info a:visited,.feed .entry .likes a,.feed .entry .likes a:visited,.feed .entry .comment a,.feed .entry .comment a:visited,.feed .entry .expandcomment a,.feed .entry .expandcomment a:visited,.feed .entry.deleted a,.feed .entry.deleted a:visited,a.fl,a.fl:visited,.usersubgrid .lists a,.usersubgrid .lists a:visited{ color: #333333 !important; }

    #header { padding-bottom: 20px !important; }

   .box .box-bar .box-bar-text, .box .box-bar .box-bar-text a { color:white !important; }

   .box-bar-text .title { color:#fff !important; }
   #container { max-width:1200px !important; }
}
