css:
@-moz-document url-prefix("https://forum.kerbalspaceprogram.com/") {
:root {
    --box-backgroundColor: hsla(240, 15%, 15%, 1.0);
    --theme-page_background: 23, 23, 28;

    --theme-button_bar: 33, 33, 44;
    --theme-area_background: 92, 92, 112;
    --theme-area_background_light: 38, 38, 64;
    --theme-area_background_dark: 43, 43, 59;
    --theme-area_background_reset: 33, 33, 44;
    --theme-tab_background: 46, 46, 56;
    --theme-selected: 61, 61, 92;

    --positive-dark:  rgb(41, 81, 41);
    --positive-light: rgb(82, 122, 82);

    --theme-main_nav_tab: 43, 43, 59;
    --theme-main_nav_tab_font: 220, 220, 255;

    --theme-tag: 61, 61, 92;
    --theme-tag_font: 220, 220, 255;
    --theme-brand_primary: 143, 143, 239;
    --theme-mentions: rgb(220, 220, 255);

    --theme-section_title: 61, 61, 92;
    --theme-section_title_font: 220, 220, 255;
    
    --theme-widget_title_bar: 61, 61, 92;
    --theme-widget_title_font: 220, 220, 255;

    --popup--boxShadow: 0 0 4px 0 hsla(240, 15%, 40%, 0.8);

    --theme-text_color: 220, 220, 255;
    --theme-text_light: 148, 148, 184;
    --theme-text_dark: 148, 148, 209;
    --theme-link: 163, 163, 245;

    --theme-timeline_color: 96, 96, 159;
    --item-timeline--boxShadow: 0 0 0 4px hsla(240, 15%, 15%, 1.0);

    --theme-light_button: 71, 71, 133;
    --theme-light_button_font: 220, 220, 255;

    --theme-very_light_button: 71, 71, 133;
    --theme-very_light_button_font: 220, 220, 255;

    --theme-primary_button: 89, 89, 166;
    --theme-primary_button_font: 220, 220, 255;

    --theme-important_button: 71, 71, 133;
    --theme-important_button_font: 220, 220, 255;
}

/* A few things are not variable based */

#elSearch {
    background-color: hsla(240, 20%, 10%, 0.8);
    color: hsla(240, 0%, 100%, 0.8);
}
.ipsReact_button {
    filter: grayscale(100%) brightness(80%);
}
::-webkit-scrollbar {
    width: 14px;
    height: 14px;
    background-color: hsla(240, 5%, 15%, 1.0);
}
::-webkit-scrollbar-button {
    display: none;
}
::-webkit-scrollbar-thumb {
    background-color: hsla(240, 20%, 30%, 1.0);
    border: 1px solid hsla(240, 10%, 25%, 1.0);
    color: hsla(240, 0%, 100%, 0.8);
}
.cke_button_icon {
    filter: brightness(300%);
}
.cke_button__ipsspoiler_icon {
    filter: invert(100%);
}
.pln {
    color: rgb(220, 220, 255);
}
.kwd {
    color: rgb(163, 163, 245);
}
.typ {
    color: rgb(245, 163, 163);
}
.com {
    color: rgb(245, 80, 80);
}
.lit {
    color: rgb(80, 163, 80);
}

/* Undo some suspicious hard coded junk that breaks the theming */

.ipsAreaBackground_reset {
    /*background-color: rgb(var(--theme-area_background_reset));*/
    background-color: rgb(33, 33, 44);
}
.ipsBox {
    background-color: var(--box-backgroundColor);
}
.ipsTabs_panel {
    /*background-color: rgb(var(--theme-area_background_reset));*/
    background-color: rgb(33, 33, 44);
}

/* Hide some useless crap */

#ipsLayout_sidebar,
footer#ipsLayout_footer {
    display: none;
}
}
