css:
@-moz-document url-prefix("https://tower.nf/") {
body {
    color: rgba(255, 255, 255, 0.87);
    background-color: #212121;
}
#sidebar {
    background-color: #212121;
    border-right-color: #2e2e2ec9;
}
.card {
    background-color: #2E2E2E;
    border-color: #585858;
}
.card .card {
    background-color: rgba(255, 255, 255, 0.05);
}

.card-header {
    border-bottom-color: #585858;
    background-color: #434343;
}
#sidebar .card {
    background-color: #2E2E2E;
    border-right-color: #2e2e2ec9 !important;
    border-top-color: #2e2e2ec9 !important;
    border-bottom-color: #2e2e2ec9 !important;
}

#sidebar .card .card-body:hover {
    background-color: #3e3e3e !important;
}
.modal-content {
    background-color: #2E2E2E;
}
.navbar-brand img,
footer img {
    filter: invert(0.5);
}
.navbar {
    background-color: #303030 !important;
}
.navbar .nav-item .nav-link {
    color: rgba(255, 255, 255, 0.6);
}
#main-tabs .nav-link,
.modal-content .nav-item a.nav-link:not(.active) {
    color: rgba(255, 255, 255, 0.6) !important;
}
.modal-footer {
    border-top-color: #585858;
}
.modal-content .nav-link.active {
    background-color: #dadada;
    border-color: #dadada;
}
.modal-content .nav-link:hover {
    border-color: #dadada;
}

#main-tabs,
#metrics-body .nav.nav-tabs,
.modal-content .nav-tabs,
.modal-header{
    border-bottom-color: #585858;
}
.modal-content pre {
    color: rgba(255, 255, 255, 0.87);
}

.modal-content .close{
    color: rgba(255, 255, 255, 0.87);
}
#metrics-body .tab-content {
    border: 1px solid #585858;
    border-top: none;
    margin-bottom: 1.25rem;
}
#main-tabs .nav-link.active,
#metrics-body .nav-tabs .nav-link.active {
    color: rgba(255, 255, 255, 0.87);
    background-color: #393939;
    border-color: #585858;
    border-bottom-color: rgba(0, 0, 0, 0);
}
.dataTables_wrapper .dataTables_filter,
.dataTables_wrapper .dataTables_paginate .paginate_button {
    color: rgba(255, 255, 255, 0.87) !important;
}
.dataTables_wrapper .dataTables_paginate .paginate_button:hover {
    background: linear-gradient(180deg, #585858 0, #434343);
    border-color: #434343;
}
.dataTables_wrapper.no-footer .dataTables_scrollBody {
    border-bottom-color: #2e2e2ec9;
}
.table {
    color: rgba(255, 255, 255, 0.87);
    background-color: rgba(255, 255, 255, 0.1);
    border-color: #2e2e2ec9;
}
.table .thead-light th{
    color: rgba(255, 255, 255, 0.87);
    background-color: rgba(255, 255, 255, 0.1);
    border-color: rgba(255, 255, 255, 0.1);
}
.table-hover tbody tr:hover {
    color: rgba(255, 255, 255, 0.9);
    background-color: rgba(255, 255, 255, 0.2);
}
.table-hover tbody tr th,
.table-hover tbody tr td{
    border-color: rgba(255, 255, 255, 0.2);
}
table.dataTable.display tbody tr.odd,
table.dataTable.display tbody tr.odd > .sorting_1 {
    background-color: rgba(0, 0, 0, 0.25);
    border-color: #2e2e2ec9;
}
table.dataTable.display tbody tr.even,
table.dataTable.display tbody tr.even > .sorting_1 {
    background-color: rgba(0, 0, 0, 0.075);
    border-color: #2e2e2ec9;
}
table.dataTable.cell-border tbody td {
    border-color: #2e2e2ec9;
}
#tasks-table-wrapper .dataTables_filter input,
#search {
    background-color: rgba(0, 0, 0, 0.05);
    border-color: rgba(255, 255, 255, 0.15);
}
.card svg {
    background-color: rgba(0, 0, 0, 0);
    filter: invert(0.775);
}
#metrics-body .plot-container svg {

    filter: invert(0.775);
}
#metrics-body .nav-link.active {
    background-color: #dadada;
    border-color: #dadada;
}
#metrics-body .nav-tabs {
    border-bottom-color: #dadada;
}
#metrics-body .nav-link:hover {
    border-color: rgba(0, 0, 0, 0);
}
section.border-bottom {
    border-bottom-color: #343434 !important;
}
section.bg-gray-200,
footer.bg-gray-200 {
    background-color: #343434 !important;
}
.svg-shim.text-gray-200 {
    background-color: #212121 !important;
    color: #343434 !important;
}
section.bg-gradient-light {
    background-image: linear-gradient(180deg, #242424 0, #212121);
}
a,
a:hover {
    color: #3498db;
}
footer {
    background-color: #212121;
}
footer .border-top.border-gray-light.text-muted {
    border-top-color: rgba(255, 255, 255, 0.25) !important;
}
}
