css:
@-moz-document domain("lbry.tv") {
::-webkit-scrollbar {
  width: 8px;
}

/* Track */
::-webkit-scrollbar-track {
  background: var(--color-header-background);
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: var(--color-input-bg);
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #555; 
}
[dir] .claim-grid__wrapper:not(:first-of-type) {
    margin-top: -56px;
}
[dir=ltr] .main-wrapper__inner {
    margin-left: auto;
    margin-right: auto;
    padding-left: 0;
    padding-right: 0;
    padding-top: 0;
    border-bottom: 0;
}
[dir=ltr] .navigation, [dir=ltr] .ads-wrapper, [dir=ltr] .navigation--absolute, [dir=ltr] .navigation--micro {
    left: 0;
    border-right: 1px solid var(--color-border);
    padding-top: 0;
    border-bottom: 0;
    background-color: var(--color-header-background);
}
[theme='dark'] {
    --color-primary-alt: #3e675d;
    --color-primary: #2bbb90;
    --color-link: var(--color-primary);
    --color-link-hover: #60e1ba;
    --color-link-active: #60e1ba;
    --color-navigation-icon: #76808a;
    --color-navigation-link: #b9c3ce;
     --color-navigation-active: #313131;
    --color-navigation-active-text: #bac5d0;
    --color-navigation-hover: #272c32;
    --color-navigation-hover-text: #bac5d0;
    --color-button-primary-bg: var(--color-primary-alt);
    --color-button-primary-bg-hover: #44796c;
    --color-button-primary-text: white;
    --color-button-primary-hover-text: var(--color-text);
    --color-button-secondary-bg: #313131;
    --color-button-secondary-border: #000;
    --color-button-secondary-bg-hover: #313131;
    --color-button-secondary-text: var(--color-text);
    --color-button-alt-bg: #313131;
    --color-button-alt-bg-hover: #313131;
    --color-button-alt-text: #e2e9f0;
    --color-header-button: #313131;
    --color-button-border: #0000;
    --color-link-focus-bg: #333a41;
    --color-focus: #2d69a5;
    --color-background: #181818;
    --color-background-overlay: #00000080;
    --color-border: #0000;
    --color-card-background: #0000;
    --color-card-background-highlighted: #0000;
    --color-header-background: #202020;
    --color-tab-text: var(--color-white);
    --color-tabs-background: var(--color-card-background);
    --color-tab-divider: var(--color-white);
    --color-modal-background: var(--color-header-background);
    --color-comment-menu: #6a6a6a;
    --color-comment-menu-hovering: #e0e0e0;
    --color-notice: #58563b;
    --color-error: #61373f;
    --color-purchased: #ffd580;
    --color-purchased-alt: var(--color-purchased);
    --color-purchased-text: var(--color-gray-5);
    --color-comment-highlighted: #484734;
    --color-thumbnail-background: var(--color-gray-5);
    --color-comment-threadline: #434b54;
    --color-comment-threadline-hover: var(--color-gray-4);
    --color-text: #d8d8d8;
    --color-text-error: #f5748c;
    --color-text-help-warning: #f5ec74;
    --color-text-empty: #bbbbbb;
    --color-text-help: #bbbbbb;
    --color-text-subtitle: #9fafc0;
    --color-text-warning: #212529;
    --color-text-warning--background: var(--lbry-yellow-1);
    --color-blockquote: var(--color-gray-5);
    --color-blockquote-bg: var(--color-card-background-highlighted);
    --color-input: #f4f4f5;
    --color-input-label: #d4d4d4;
    --color-input-placeholder: #f4f4f5;
    --color-input-bg: #313131;
    --color-input-bg-copyable: #4f5861;
    --color-input-border: var(--color-border);
    --color-input-border-active: var(--color-secondary);
    --color-input-toggle: var(--color-primary);
    --color-input-toggle-bg: var(--color-input-bg);
    --color-input-toggle-bg-hover: var(--color-secondary);
    --color-input-bg-selected: var(--color-primary-alt);
    --color-menu-background: var(--color-header-background);
    --color-menu-background--active: #3a4045;
    --color-menu-icon: #a7a7a7;
    --color-menu-icon-active: #464d53;
    --color-table-highlight: #3a444e;
    --color-search-suggestion: var(--color-text);
    --color-search-suggestion-background: #313d46;
    --color-placeholder-background:  var(--color-header-background);
    --color-spinner-light: #5a6570;
    --color-spinner-dark: #212529;
    --color-wunderbar: #89939e;
    --color-tag: var(--color-primary);
    --color-tag-bg: var(--color-primary-alt);
    --color-tag-hover: var(--color-white);
    --color-tag-bg-hover: var(--color-primary-alt-2);
    --color-snack-bg: var(--color-secondary);
    --color-editor-cursor: var(--color-text);
    --color-editor-quote: #d3d3d3;
    --color-editor-tag: #efbe5d;
    --color-editor-attr: #68ccf9;
    --color-editor-string: #ff8b6b;
    --color-editor-inline-code-fg: #ce9178;
    --color-editor-inline-code-fg-preview: #eeeeee;
    --color-editor-inline-code-bg: rgba(20, 68, 102, 0.3);
    --color-editor-inline-code-bg-preview: #8f9499;
    --color-editor-selected: #264f78;
    --color-editor-link: var(--color-link);
    --color-editor-url: var(--color-editor-string);
    --color-editor-hr: var(--color-editor-tag);
    --color-editor-hr-preview: #a0a0a0;
    --color-ads-background: #475057;
    --color-ads-text: #111;
    --color-ads-link: var(--color-primary-alt);
}
}
