css:
@-moz-document domain("psi.xaa.pl"), domain("forum.psi.xaa.pl") {
img {
    transform: none !important;
    border-radius: 0 !important;
}

.stats_count {
    transform: none !important;
}

img {
	border: 3px solid transparent !important;    
}

#forums_body > #forums_row {
    border-bottom: 1px solid #101010 !important; 
}

#page_panel > .page_left > .panel_avatar > .panel_avatar_status > img, 
#forums_body > #forums_row > .forums_profile_av > .forums_profile_status > img, 
#page_right > .sitebar > .sitebar_body > .sitebar_row > .sitebar_av > .sitebar_av_status > img {
    border: none !important;
    margin-right: 0 !important;
}
#page_panel > .page_left > .panel_avatar > .panel_avatar_status {
	margin-left: 102px !important;
    border-radius: 100%;
    border: solid 1px black;
}

#forums_body > #forums_row > .forums_profile_av > .forums_profile_status {
	margin-left: 47px !important;
    background: #696969 !important;
    border-radius: 100%;
    border: solid 1px #191919;
}

#page_right > .sitebar > .sitebar_body > .sitebar_row > .sitebar_av > .sitebar_av_status {
    background: #696969 !important;
    border-radius: 100%;
    border: solid 1px #272727;
}

body {
    background-color: #131313;
}

#page_panel > .page_left,
#page_panel > .page_right,
#forums_body > #forums_row,
#page_right > .sitebar > .sitebar_body,
td.row1, .sb2, .code,
#page_shoutbox > #page_shoutbox_row,
#page_viewonline > .page_viewonline_row,
#page_birthday > .page_birthday_row {
	background-color: #232323 !important;
}

#page_right > .sitebar > .sitebar_body > .sitebar_row:nth-child(4n+3),
#forums_body > #forums_row_bottom, 
td.row2, .sb1, 
#page_shoutbox > #page_shoutbox_row_bottom, 
#page_viewonline > .page_viewonline_row_bottom,
#page_birthday > .page_birthday_row_bottom  {
    background-color: #191919 !important;
}

#page_right > .sitebar > .sitebar_title,
input.post, textarea.post, select, input.post2, textarea.post2, select,
#pager_button > a, .button {
	background-color: #101010 !important;   
}
}
