css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
/* tweet content space including the entry form background */
#content {background-color:#000000 !important; }
div.tab {background-color:#000000 !important; }
div.bottom-nav {background-color:#000000 !important; }
#status_update_form .info {background-color:#000000 !important;}
#status_update_form textarea {background-color:#555555 !important;}
/* tweets with no hovering pointer */
ol.statuses {background-color:#000000 !important; }
/* tweets with pointer hovered */
ol.statuses li:hover {background-color:#ffffff !important; }
}

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {

#following_list, #following_list span {
  padding: 0 !important;
  width: 100%;
}

#following_list {
  margin: 5px 0 0 2px !important;
}

#following_list a[rel=contact] {
  display: block;
  margin: 0 0 5px 0 !important;
  height: 24px;
}

#following_list a[rel=contact] {
  padding-left: 13px !important;
}

#following_list a[rel=contact] img {
  display: block;
  float: left;
  margin: 0 8px 0 0 !important;
}

#following_list a[rel=contact]:after {
  content: attr(title);
  font-family: "Lucida Grande", sans-serif;
  font-size: .9em;
  font-weight: bold;
  line-height: 24px;
}

#following_list a[rel=contact]:hover {
  text-decoration: underline;
}

}

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.twitter.com), url-prefix(http://twitter.com), url-prefix(http://www.twitter.com/*), url-prefix(http://www.twitter.com/*) {
  .wrapper {
    padding-bottom: 8px !important;
  }

  #container {
    width: 90% !important;
  }

  #content {
    width: 1000px !important;
  }

  #onoff, .promotion, #device_updates, .yellow-box {
    display: none !important;
  }

  .info {
    padding: 0 10px !important;
  }

  #status-field-char-counter {
    margin-right: -5px !important;
  }

  #status {
    width: 100% !important;
    margin-top: 5px !important;
  }

  #currently {
    width: 65% !important;
  }

  .bulletin {
    display: none !important;
  }

  .section {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
  }

  .status-body {
    width: 80% !important;
  }

  .stats {
    padding-bottom: 10px !important;
  }
}
