/* ==UserStyle==
@name           PlayStation Store (Redesigned) - Dark Theme
@version        0.1
@description    New PS Store theme is eye-burning, so here's a simple dark theme. It might not fully cover all areas yet, but homepage, search results and product details.
@author         anorak
==/UserStyle== */

@-moz-document domain("store.playstation.com") {
    .ems-sdk-product-tile .price {
    color: #eee;
    }
    
    .psw-root body {
    color: #fff;
    fill: #fff;
    background-color: #000;
    }
    
    #jetstream-tertiary-nav, #shared-nav-container{
        fill: #000;
    }
    
    .search-container{
        color: #000;
    }
    
    .search-results h1 em{
        color: #eee;
    }
    
}
