@-moz-document url-prefix(https://workona.com) {
	/* Main space background */
	.app, ._2jeXtzABUskiV3epT_mV16, ._1B07DczOsH0lLKXSUKKLSv {
		background: #000000 !important;
	}

	/* Context menus, tabs list and popups background colour */
	._3sltFBVBoMEsYM854YBJBf, ._14zN2To0Vr4ObISm0xpjUl, ._3xx-hM_fYDCpigf65kf6CG, ._3irQB9Tv18T_XmnsCBQ3rF,
	.dNnn60S2BTE7NEfnPoVJZ:hover, ._1huIutIXFnXbWly3-8gzrN, ._2UgL7qBuTYlPeBMIv3Z44a, ._19WklTkm6zF3OwDBD-TkyC, ._2y4UB7JoIIMx_mHGLneqZk, .UpWcfG5YipBjRVGTZ7mh3, ._72TFG8gHnPcEg0mSsHlNF, ._3RftQo69y1bWp4mlOm-PJi, ._3z-5ASxElLkMufBKh557vK {
		background: #1e1e1e !important;
	}

	/* Foreground colour (main text and placeholder) */
	.dNnn60S2BTE7NEfnPoVJZ:hover, ._1huIutIXFnXbWly3-8gzrN, ._3iUg1tZYNsBSwhqmAXm8Zu, ._13Hjg8UL7yInNWmASkLjoj, ._2kRbqWMhyJB1R51jTa19LL, ._2j42J7zEfGyX8f6i760A9Y {
		color: #cccccc !important;
	}

	/* Main foreground colour */
	body, ._1gJ_NYaKZe2lAgqgtY3yDn, .KPACyk1OtTz9LvyR9_T6P {
		color: #cccccc;
	}

	/* Sidebar background */
	/* Preferences sidebar background */
	/* Add resource sidbar */
	._1MqP5l6V_u1Kza6KH4OeI7, .De30U_-srldDdjYCLcN8_, ._1S2kJtgA8OLNPTN4cbi21R, .tIEHl6uvpqYX65PnAC-DJ {
		background: #181818;
	}

	/* Find overlay background */
	._2NmaFledpPHzSsOzFAZTlZ {
		background: rgba(0,0,0,.50);
	}

	/* Preferences main space background */
	._3z-5ASxElLkMufBKh557vK, ._37cWZUU1YOl71VNsGGW6Tr {
		background: #1e1e1e;
	}

	/* Preferences email address text box foreground colour */
	/* (greyed out / disabled) */
	.MuiInputBase-root {
		color: grey !important;
	}

	/* Preferences text box foreground colour */
	/* (enabled) */
	._2ihVv2nYKWlUMtU61rb2OI:not(.Mui-disabled), .vL3c5V77Fjk7XlAOOhpwW {
		color: #cccccc;
	}

	/* Preferences text box label foreground colour */
	.MuiFormLabel-root, .MuiFormLabel-root.Mui-disabled, ._2y4UB7JoIIMx_mHGLneqZk {
		color: #aaaaaa;
	}

	/* Find search (in sidebar) and + button background colour */
	._1wUbfJlwgeEwnEO65dUZFo, ._22o5f_EuPgID_SD9A00E {
		background: #252525;
	}

	/* Find search (in sidebar) and + button background colour on hover */
	._1wUbfJlwgeEwnEO65dUZFo:hover, ._22o5f_EuPgID_SD9A00E:hover {
		background: #323232;
		/* 	color: #ccc; */
	}

	/* Selected workspace background in sidebar */
	._11UI4TMMMq_B3mdw5saK6y {
		background: #323232 !important;
	}

	/* Hovering tab list background (hover on a workspace name in sidebar) */
	._2ViXpP4VnNTHEbxAje4dhn {
		background: #1e1e1e;
	}

	/* Hovering tab list hovered item background */
	._76hFX1t9pYcX9b8358iHo {
		background: #101010;
	}

	/* Hovering tab list header background */
	._3-EMxNRyLc1LFA2z5epBAk {
		background: #101010;
	}

	/* Hovering tab list header background */
	._3YD0drq3mR1yIDofo1eKHC:hover {
		background: #101010;
	}

	/* In sidebar: Background colour of workspace name when the user hovers over it */
	._3YD0drq3mR1yIDofo1eKHC:hover, ._2t5Vn-jCkilRvSaokPF9yY {
		background: #252525;
	}

	/* + buttons: Hovering on items: background and foreground */
	._2y44mkYNI7jB1qleu0CSp3:hover {
		background: #181818;
		color: #ccc;
	}

	/* Checkbox tick (foreground) colour */
	._1nE8W71-1pNzo-wyc8sNKn ._1ZDGfmPAg4CHW7-wx5fn8j {
		background: #000000;
	}

	/* Unselected checkbox (the square/outline) colour) */
	._1ZDGfmPAg4CHW7-wx5fn8j {
		box-shadow: inset 0 0 0 1px #3370ff;
	}

	/* Tab handle in tab list that is used to re-order the tabs */
	._15h5M7nKRx9TIS-dHwx5QI {
		filter: invert(100%);
	}

	/* Darken the tab images on unnamed workspaces and folder images on workspaces without open tabs */
	._2XKZpLLi4sxYN4SUe88Ntg, ._3-zAr9MbJSVOGq8zKmW0MM {
		filter: grayscale(100%) invert(95%) brightness(0.2);
	}

	/* Remove opacity of some text foreground colour (e.g. `I’m not sure yet` in `What do you think of Workona?` pop-up) */
	._2rlpEV4yAlaHiNp2fSz3IP {
		opacity: 1;
	}

	/* Material icons foreground colour */
	.material-icons {
		color: #3370ff !important;
	}

	/* Material icons: Checkmark in checkbox should be white */
	.nU0BVgRBjFu8th9lWpc4- .material-icons {
		color: #ffffff !important;
	}

	/* Add resource search background colour */
	._3h6NibiE3OxNs7m2ibGCVR, ._3DTu0bRWFH2zoCMBSr3OY6 {
		background: #252525;
	}

	/* Remove new tab addon recommendation */
	.BsMaM9O7k_AslX_3d68A0 {
		visibility: hidden
	}

	/* Keyboard shortcuts buttons foreground colour in Workona preferences */
	._14QA6-dt-6-kJYYtkZNh6b {
		color: #cccccc;
	}

	/* Keyboard shortcuts buttons background colour in Workona preferences */
	._13BMhdRgXbiwDau0BTeVQ0 {
		background-color: #000000;
	}

	/* The foreground colour of the info above the keyboard shortcuts buttons in Workona preferences */
	._27R_cYwtZ7GXrD_Kc_izjg {
		color: #cccccc;
	}

	/* The background colour of the info above the keyboard shortcuts buttons in Workona preferences */
	._27R_cYwtZ7GXrD_Kc_izjg {
		background-color: #000000;
	}
}
