css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("usemycomputer.com") {
/* ALL, line-height */
body { 
line-height: 18px !important;
}

/* ALL, background color */
body {
background: #F9F9F9 !important;
}

/* ALL, Link colors */
a:link { color: #444 !important; text-decoration: none !important; }
a:hover { color: #000000 !important; text-decoration: underline !important; }
a:visited { color: #4E443C !important; text-decoration: none !important; }
a:active { color: #000000 !important; text-decoration: none !important; }

/* ALL, Hide ADS */
DIV[class="rcol"] { 
display: none !important;
}

/* HEADER, size */
BODY > DIV.header {
margin: 15px 5px 0px 0px !important;
background: #F9F9F9 !important;
}

/* HEADER, font style */
BODY > DIV.header > DIV.name > A,
BODY > DIV.header > DIV.navigation > UL > LI > A ,
BODY > DIV.header > DIV.navigation > UL > LI > SPAN.iphouse > A {
background: none !important;
font-size: 28px !important;
font-family:Georgia,serif !important;
color:#4E443C !important;
font-variant: small-caps !important;
text-transform: none !important;
font-weight: 100 !important;
margin: 0px 0px 0px 0px !important; 
padding: 0px 0px 0px 0px !important;
}

/* HEADER, font hover */
BODY > DIV.header > DIV.navigation > UL > LI > A:hover ,
BODY > DIV.header > DIV.navigation > UL > LI > SPAN.iphouse > A:hover {
text-decoration: underline !important;
}

/* HEADER, div text */
BODY > DIV.header > DIV.name > A:after {
content: " (for Daily Photo klick bellow)" !important;
color:#600 !important;
font-size: 14px !important;
margin: 0px 0px 0px 30px !important;
}

/* INDEXIMAGE, div size */
BODY > DIV.indeximage {
height: 15px !important;
background: #600 !important;
margin: 0px 15px 0px 15px !important;
padding: 0px 0px 0px 0px !important;
}

/* INDEXIMAGE, div hover */
BODY > DIV.indeximage:active {
height: auto !important;
background: #F9F9F9 !important;
}

/* INDEXIMAGE, image margin*/
BODY > DIV.indeximage > IMG[alt="Indeximage"] {
margin: 20px 0px 0px 0px !important;
}

/* BODY, fill page */
BODY > DIV.middle,
BODY > DIV.middle > DIV.lcol {
width: 100% !important;
}

/* POST, fill page */
BODY > DIV.middle > DIV.lcol > DIV.post { 
padding: 0px !important;
margin: 0px 15px 0px 15px !important;
}

/* POST, no borders */
BODY > DIV.middle > DIV.lcol > DIV.post { 
border: none  !important;
}

/* POST, fonts */
BODY > DIV.middle > DIV.lcol > DIV.post {
font-family: "Helvetica Neue", "Lucida Grande", Helvetica, Arial, Verdana, sans-serif !important;
font-size: 22px !important;
margin-top: .5em; color: #666 !important;
}

/* POST, change icons */
BODY > DIV.middle > DIV.lcol > DIV.post > P > A > IMG[src^="/ui/lr.png"] { margin: -5px 5px 0px 5px !important; height: 16px !important; width: 16px !important; padding-left: 16px !important; background-image: url(data:image/png;base64,) !important; width: 0px !important; background-repeat: no-repeat !important; }
BODY > DIV.middle > DIV.lcol > DIV.post > P > A > IMG[src^="/ui/ll.png"] { margin: -5px 5px 0px 5px !important; height: 16px !important; width: 16px !important; padding-left: 16px !important; background-image: url(data:image/png;base64,) !important; width: 0px !important; background-repeat: no-repeat !important; }
BODY > DIV.middle > DIV.lcol > DIV.post > P > A > IMG[src*="/linkimages/"] { margin: -5px 5px 0px 5px !important; height: 16px !important; width: 16px !important; }

/* PERMALINK, fonts */
BODY > DIV.middle > DIV.lcol > DIV.post > DIV.meta > P {
font-family: "Helvetica Neue", "Lucida Grande", Helvetica, Arial, Verdana, sans-serif !important;
font-size: 12px !important;
color: #4E443C !important;
background: #4E443C !important;
margin-bottom: 10px !important;
}

/* PERMALINK, links */
BODY > DIV.middle > DIV.lcol > DIV.post > DIV.meta > P > A {
color: #4E443C !important;
}

/* PERMALINK, hover */
BODY > DIV.middle > DIV.lcol > DIV.post > DIV.meta > P:hover {
background: #F9F9F9 !important;
}

/* TOP20, H1 style */
BODY > DIV.middle > DIV.lcol > DIV.post > P > EM {
background: none !important;
font-size: 26px !important;
font-family:Georgia,serif !important;
color:#4E443C !important;
font-variant: small-caps !important;
text-transform: none !important;
font-weight: 100 !important;
}

/* TOP20, H2 style */
BODY > DIV.middle > DIV.lcol > DIV.post > P > A[href^="/indeximages/"] {
font-variant: small-caps !important;
}
BODY > DIV.middle > DIV.lcol > DIV.post > P > A[href^="/out/"] {
font-variant: small-caps !important;
}

/* MORELESS, style */
BODY > DIV.middle > DIV.lcol > DIV.post > DIV.moreless > P {
background: none !important;
font-size: 56px !important;
font-family:Georgia,serif !important;
color:#4E443C !important;
font-variant: small-caps !important;
text-transform: none !important;
font-weight: 100 !important;
text-align: justify !important;
position: relative !important;
padding: 10px !important;
margin: 10px !important;
}

/* MORELESS is more */
DIV.post > DIV.moreless > P > A:first-child:after {
content: " is" !important;
font-size: 26px !important;
}

/* SEARCH, text style */
BODY > DIV.middle > DIV.lcol > DIV.post > DIV.search > FORM > P > INPUT[type="text"] {
background: #4E443C !important;
color: #FFF !important;
}

/* SEARCH, area style */
BODY > DIV.middle > DIV.lcol > DIV.post > DIV.search {
background: #4E443C !important;
padding: 10px !important;
margin: 0px !important;
height: 40px !important;
text-align: left !important;
}

/* FOOTER, style */
BODY > DIV.middle > DIV.lcol > DIV[style="clear: both;"] > P {
background: #4E443C !important;
padding: 10px !important;
margin: 0px 0px 10px 0px !important;
}
}
