css:
@-moz-document domain("store.playstation.com") {
body, .psw-root body, .tertiary-tabs {
    color:white;
    fill: white;
    background-color: #000;
    padding:0 0 0 0;
}

/* Price Title */
.price, .ems-sdk-product-tile .price {
    color:white;
    font-weight:bold;
}

/* Hide Featured Game */
.ems-sdk-blueprint-hero {
    display:none;
}

/* Main Store Top Nav Bar */

#jetstream-tertiary-nav {
    background-color:black;
    fill:black;
}

#jetstream-tertiary-nav .tertiary-menu-cta, #jetstream-tertiary-nav .tertiary-tab__link, #jetstream-tertiary-nav .tertiary-tab__link:focus, #jetstream-tertiary-nav .tertiary-tab__link:hover, #jetstream-tertiary-nav .tertiary-tab__link:visited {
    color:white;
    background-color:black;
    fill:black;
}

[dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link, [dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link:hover, [dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link:focus, [dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link:visited, [dir] #jetstream-tertiary-nav .tertiary-container:not(.tertiary-container--overflow) .tertiary-tab__link.tertiary-tab__link--active {
    color:white;
    background-color:black;
    fill:black;
}

#jetstream-tertiary-nav {
    fill: white;
}

/* Front Featured Game Square Size */

html .grid-margin-x.tablet-l-up-3 > .cell {
    width:calc(12% - 0rem);
}

html .grid-margin-x > .cell {
    width:calc(12% - 0rem);
    margin-left:0.5rem;
    margin-right:0.5rem;
}

/* Top Sony Bar */


/* Top Sony Bar
#sony-bar, #shared-nav-root {display:none;}*/

#sony-bar, #shared-nav-root {
    background-color:black;
    fill:black;
}

[dir] #shared-nav-container, [dir] #shared-nav .shared-nav__primary-parent {
    background-color: black;
    color:white;
}

#shared-nav-container.shared-nav-container--minimized .shared-nav__primary-button, #shared-nav-container.shared-nav-container--minimized #sb .sb-my-playstation-link, #shared-nav-container.shared-nav-container--minimized .sb-skeleton-my-playstation-link {
    color:white;
}
}
