css:
@-moz-document url-prefix("http://roblonium.com/"), url-prefix("http://roblonium.tk/"), url-prefix("http://sitetest1.roblonium.com/") {
#Footer,
#MasterContainer,
#BodyWrapper {
    background: none !important;
}
#testingSitePanelWrapper {
    background: none !important
}
#NavigationRedesignBannerContainer.BannerCenterContainer a.btn-logo,
#NavigationRedesignBannerContainer.BannerCenterContainer a.btn-logo:visited,
#NavigationRedesignBannerContainer.BannerCenterContainer a.btn-logo:hover {
    background-image: url(https://oldminecraft.neocities.org/shityobcimade.png);
    width: 157px;
    margin-left: 0px;
}
#NavigationRedesignBannerContainer .HeaderDivider {
    border-left: 1px solid #878988
}
#Banner.BannerRedesign {
    background-image: url(http://roblonium.com/images/RevisedHeader/bg-obc_header.png)
}
#Footer.unfixedFooter {
    background: transparent
}
#RepositionBody {
    background-color: transparent;
}
#Footer.LanguageRedesign div.legal {
    border-color: white;
}
#Footer.LanguageRedesign p.Legalese {
    color: #555 !important;
}
body {
    background-color: black !important;
}
body,
body.bodyUnfixedHeader {
    background: url(https://s3.amazonaws.com/images.roblox.com/c6841e6081b2136e9af45152a68751af) top center repeat-x black;
}
#SmallHeaderContainer div#Banner {
    background-color: #2E2E2E;
}
a.btn-logo {
    background-image: url(https://s3.amazonaws.com/images.roblox.com/4d10a931b159051418e34be0e5dc4bf9);
}
#Header .Navigation {
    background: url(https://s3.amazonaws.com/images.roblox.com/f73bca437c962753552fa7102e87ed07);
}
.Navigation li:hover {
    background: url(https://s3.amazonaws.com/images.roblox.com/f73bca437c962753552fa7102e87ed07);
    background-position: 0 30px;
}
}
