License: No Redistribution 
Version: 1.0 
*/
html, body {
    background-color: #181818; background: #181818; }

.content-page {
    background: #181818; }

.content-box {
    background: #646363; }

.site-header {
    background-color: #202020; }

.site-header .sitewide-search .button {
    background: #e67c35; }

.site-header .main-menu li .count-badge {
    background: #e67c35; }

.gamer-sidebar-block .block-header.press {
    background: #e67c35; }

::-webkit-scrollbar {
    width: 10px; }

::-webkit-scrollbar-track {
    background: #333232; }

::-webkit-scrollbar-thumb {
    background: #888; }

::-webkit-scrollbar-thumb:hover {
    background: #555; }

a, span, a span, div, .available {
    text-decoration: none; color: #ccc; }

.jump-to, a:hover, a span:hover {
    color: #fff; text-shadow: 0 0 3px #000, 0 0 1px #666; }

.string, .required, .required:hover, .required:focus, input, input:hover, input:focus, select, select:hover, select:focus, option, option:hover, option:focus, select:hover, select:focus {
    border-bottom: 1px solid rgba(255, 255, 255, .18); border-radius: 3px; box-shadow: inset 0px 1px 5px 0px #000; border-top-color: #000; border-right-color: #111; border-left-color: #111; background-color: #141414; color: #fff; }

.game-description {
    color: #fff; }

.game-description :link {
    text-decoration: underline; color: #e67c35; }

.block-body .collapse {
    background-color: #333; }

.game-details, .discover-game {
    background-color: #2f2d2d; }

.small-12 h3 {
    color: #e67c35; font-weight: bold; }

.discover-game.game-wrapper {
    border: 1px solid rgba(181, 181, 181, .18); background-color: #2f2d2d; }

.discover-game-list.stacking-list .discover-game .game-details h4.game-name a, .discover-game-list .discover-game .game-details h4.game-name a {
    color: #e67c35; }

.discover-game-list .discover-game .press-access .available, .discover-game-list .discover-game .press-access .unavailable {
    color: #848484; }

.white.button.request-key {
    border-bottom: 1px solid rgba(255, 255, 255, .18); border-radius: 3px; border-top-color: #000; border-right-color: #111; border-left-color: #111; background-color: #53524f; color: #b7b7b7; }

.white.button, .white.button:focus {
    border-bottom: 1px solid rgba(255, 255, 255, .18); border-radius: 3px; border-top-color: #000; border-right-color: #111; border-left-color: #111; background-color: #202020; color: #fff; }

.white.button:hover {
    background-color: #e67c35; color: #fff; }

.view-more {
    color: #fff; background: linear-gradient(to bottom, #404040 0%, #0d0d0d 100%); }

.discover-game-list .discover-game .game-tag-list .tag.affiliate {
    background-color: #3c72aa; color: white; }

.discover-game-list .discover-game .game-tag-list .tag.sponsored {
    background-color: #642323; color: #ff2929; }

.discover-newsfeed {
    border: 1px solid rgba(181, 181, 181, .18); background-color: #151515; }

.discover-newsfeed ul li a:hover {
    background-color: #090c1c; }

.discover-newsfeed .game-title {
    color: #e67c35; }

.site-footer {
    background: #2d2d2d; }

.site-header #profile-menu {
    background-color: #2f2d2d; color: #ffffff; }

.site-header #profile-menu a {
    background-color: #2f2d2d; color: #e67c35; }

.site-header #profile-menu a:hover {
    background-color: #646363; }

.pagination li {
    background-color: #2f2d2d; }

.pagination a:visited, .pagination a:link {
    color: white; }

.pagination .current {
    background: #e67c35; }

.grey-block {
    background-color: #333; color: white; }

.check_boxes label, .radio {
    background-color: #333; color: white; }

.select.optional {
    color: #e67c35; }

.white-block {
    background-color: #2f2d2d; border: 1px solid rgba(181, 181, 181, .18); }

.tabs-panel {
    color: #bfbfbf; }

.tabs-title a {
    color: #fff; }

.gamer-keys .key-table .row.even {
    background-color: #424242; }

.gamer-keys .key-table .title-column h4 a {
    color: #fff; }

.gamer-keys .key-table {
    background-color: #262626; }

.gamer-keys .key-table .title-column span.affiliate {
    color: #e67c35; }

.tabs .tabs-title.is-active a {
    color: #fff; background-color: #626262; }

.tabs .tabs-title {
    background-color: #202020; }

.tabs.vertical {
    background-color: #202020; }

.tiny.button.success {
    background-color: #3c72aa; }

.tiny.button.alert {
    background-color: #823a3a; }

.tiny.button.yellow {
    background-color: #e67c35; border-color: #e67c35; color: #000; }

.callout {
    color: #fff; }

.currency-usd {
    color: #86caff; font-weight: bold; }

.table-scroll.money-results thead tr th {
    background-color: #282828; color: #fff; }

table.money-results tbody tr td.aff-results {
    background: #572fa8; color: #e67c35 }

table.money-results tbody tr:nth-child(odd) td {
    background: #393939 }

table.money-results tbody tr:nth-child(odd) td.started {
    background: #4f4f4f }

table.money-results tbody tr:nth-child(odd) td.schema {
    background: #555 }

table.money-results tbody tr:nth-child(odd) td.commission {
    background: #606060 }

table.money-results tbody tr:nth-child(odd) td.clicks {
    background: #4f4f4f }

table.money-results tbody tr:nth-child(odd) td.sales {
    background: #606060 }

table.money-results tbody tr:nth-child(odd) td.future {
    background: #4f4f4f }

table.money-results tbody tr:nth-child(odd) td.previous {
    background: #606060 }

.game-requirements {
    color: #c4c4c4; }

.sidebar-block {
    color: #e67c35; }

.block-footer {
    background-color: #646363; font-weight: bold; border: none; }

.block-footer h5 {
    color: #fff; }

.collapse {
    background-color: #646363; }

.collapse h5 {
    color: #fff; }

.gamer-steam-account .label.success {
    background-color: #e67c35; }

.button.success, .button.success.disabled, .button.success[disabled], .button.success.disabled:hover, .button.success[disabled]:hover, .button.success.disabled:focus, .button.success[disabled]:focus {
    background-color: #000; color: #fff; }

.gamer-game-stats {
    margin-bottom: 32px; background: #282828; border: 1px solid rgba(181, 181, 181, .18); }

.gamer-profile .audience-stats .audience.grid {
    border-bottom: 1px solid #4f4a4a; }

.gamer-profile .audience-stats .audience {
    border-right: 1px solid #4f4a4a; padding-top: 6px; padding-right: 8px; }

.social-block .content-item {
    background: #4a4a4a; padding: 16px; margin-left: 72px; }

.social-block .content-item h5 a {
    background: #373737; color: #cecece; }

.small-4 .title {
    background-color: #4a4a4a; }

.simple-block {
    background-color: #646363; }

.simple-block-content, .hint {
    color: white; }

.simple-block-footer {
    background: #373737; }

.simple-block .simple-block-footer {
    background: #282828; border-top: 1px solid #8e8e8e; padding: 16px 0; }

.no-data-block {
    background-color: #4f4a4a; }

.info-block {
    background: #373737; }

.info-block-content {
    background: #373737; }

.info-block-header {
    background: #373737; }

.info-block .info-block-header {
    background: #646363; padding: 0.75rem 1rem; }

.messaging .conversations .conversation {
    display: table-row; background: #282828; color: #666; }

.messaging .receipts .receipt.mail .tr .meta-top {
    background: #797979; }

.messaging .receipts .receipt.mail .tr .meta-top .when {
    color: #fff; }

.messaging .receipts .receipt.mail .tr .meta-top .name .org_name {
    color: #fff; }

