.help-faq-list .help-faq-section .help-faq-content {
    background: #646363; }

.help-faq-list .help-faq-section .help-faq-content .help-faq .faq-name {
    color: #e67c35; }

.help-faq-list .help-faq-section .help-faq-content .help-faq .faq-content {
    color: #fff; }

.c0, .c1, .c9, .c13, .c3, .c10, .c4, .c2, .c11, .c7, .c18, .c15 {
    color: white; }

.c5 {
    color: white; font-weight: bold; }

.content-box table tbody {
