.messaging .conversations .conversation:hover, .messaging .receipts .receipt.mail .tr .meta-top:hover {
    background: #e67c35; }

.help-guide-list .help-guide .guide-footer {
    background: #282828; }

.reveal-overlay {
    background-color: rgba(0, 0, 0, 0.8); }

.guide-block .guide-block-header {
    background: #646363; }

.guide-block-content {
