.game-key {
    background: #282828;
    display: block;
    text-align: left;
    font-size: 1.4rem;
    padding: 8px 16px;
    margin: 16px 0 32px 0;
    font-family: monospace;
}
.reveal-key p.hashtag b {
    background: #282828;
}
.simple-block .simple-block-header {
    background: #646363;
    border-bottom: 2px solid #e67c35;
    padding: 16px 0;
    text-align: center;
    border-radius: 6px 6px 0 0;
}
.title-pink {
    color: #e67c35;
}
