css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

body, input, textarea, select, option, button, th, td {
	font-family: Tahoma !important;
	font-size: 12px !important;
}

body {
	background-image: none !important;
	background-color: #ffeff2 !important;
	color: #333 !important;
}

a {
	text-decoration: none !important;
	color: #f46497 !important;
}

a:hover {
	color: #903a58 !important;
}

#body h1, #body p {
	color: #333 !important;
}

.subhead img {
	background-color: #fff !important;
	padding: 5px !important;
	-moz-border-radius: 8px !important;
}

#container {
	background: #ffdce3 url(http://friendfeed-media.com/6e1b32a70e844bc93d4050e5a568a4810f1f785f) repeat-x top !important;
	color: #777 !important;
	border: 3px solid #f8cfd7 !important;
	padding: 8px !important;
}

.logo a img {
	display:none !important;
}

.logo a {
	width: 234px !important;
	height: 57px !important;
	display: block !important;
	background: url(http://friendfeed-media.com/1c5634e89067e4850a1c38b21044a8ce44d1474c) no-repeat top !important;
	margin: 0 !important;
	padding: 0 !important;
}

.header {
	border: none !important;
	background-color: #fbd8de !important;
	-moz-border-radius: 8px !important;
	padding: 5px !important;
}

img.picture {
	border: 1px solid #af7b85 !important;
}

.header .stats {
	font-size: 11px !important;
	line-height: 140% !important;
}

.header h1 {
	font-family: Arial !important;
	font-weight: bold !important;
	font-size: 23px !important;
	margin-top: 10px !important;
}

.header h1 a {
	color: #fff !important;
	background-color: #f5c3cc !important;
	padding: 3px !important;
	-moz-border-radius: 5px !important;
}

.servicespreview a img .icon {
	border: 1px solid #000 !important;
}

.box-top, .box-bottom {
	background: none !important;
	display: none !important;
}

.body h1 {
	font: bold 23px Arial !important;
}

.body .l_editprofile {
	font: 11px Tahoma !important;
}

.subscribebar a, .subscribebar a:hover, .box-bar-text a, .box-bar-text a:hover {
	color: #fff !important;
}

.subbar {
	color: #333 !important;
}

.box-body {
	padding-top: 30px !important;
	background-color: #ffe8ed !important;
	margin-bottom: 35px !important;
	-moz-border-radius: 8px !important;
}

#alertmessage #infomessage {
	color: #333 !important;
}

.box-body h1 {
	font: bold 23px Arial !important;
}

.box-body h2 {
	font: bold 19px Arial !important;
}

.box-body h3 {
	font: bold 17px Arial !important;
}

.category h4 {
	color: #333 !important;
}

.l_tolist {
	border: 1px solid #ffe8ed !important;
	color: #333 !important;
}

.l_tolist input {
	margin-right: 10px !important;
}

.l_tolist .l_tocard {
	font-size: 12px !important;
}

.sharebox {
	border: none !important;
}

.sharebox .textbox textarea {
	background: url(http://friendfeed-media.com/99f4c9aaa6c52eb8db9832e6707a61127fdb95a5) no-repeat center !important;
}

.sharebox .textbox textarea:focus {
	background-image: none !important;
}

embed#fileuploader {
	display: none !important;
}

.pager {
	border: none !important;
}

.l_profile img {
	border: 1px solid #af7b85 !important;
}

.entry {
	background-color: #ffeef1 !important;
	border-top: 3px solid #ffdfe5 !important;
	border-bottom: 3px solid #ffdfe5 !important;
	border-left: 2px solid #ffdfe5 !important;
	border-right: 2px solid #ffdfe5 !important;
	padding: 10px !important;
	-moz-border-radius: 8px !important;
	margin-bottom: 35px !important;
}

.entry .title .name a.l_profile {
	font: bold 12px Tahoma !important;
}

.title .name:after {content: 'Ù…ÛŒâ€ŒÚ¯Ù‡: ' !important}

.entry .body .title .text {
	font: 13px Tahoma !important;
	color: #523b40 !important;
	margin-bottom: 15px !important;
	text-align: justify !important;
	line-height: 170% !important;
}

.thumbnail {
	border: 1px solid #f2b3c0 !important;
	background-color: #fff !important;
}

.body .info {
	font: 11px Tahoma !important;
	color: #888 !important;
}

.entry .likes {
	margin-right: 0px !important;
	font-size: 12px !important;
	color: #888 !important;
}

#feed .comments {
	font: 12px Tahoma !important;
	padding: 5px !important;
	-moz-border-radius: 5px !important;
	margin: 10px 5px !important;
}

.entry .comment {
	font: 12px Tahoma !important;
	line-height: 180% !important;
}

.entry .comment .content {
	padding: 3px 0 !important;
	color: #777 !important;
}

.friend {
	background-color: #fee9ed !important;
	border: 1px solid #ffdfe5 !important;
	-moz-border-radius: 5px !important;
    padding: 2px !important;
}

.owner {
	background-color: #fff6f8 !important;
	border: 1px solid #fff !important;
	-moz-border-radius: 5px !important;
}

#feed .comments .content {
	overflow: hidden !important;
}

.comments .commentform form .text textarea {
	background-color: #fffbd0 !important;
	border: 1px solid #e6bfc7 !important;
	color: #4e4e4e !important;
}

.ccount, .ccount span {
	color: #ccc !important;
}

.searchbold {
	color: #ff5418 !important;
}

div.box li a[href*=direct].unread {
	color: #ff5418 !important;
}

.section {
	border-bottom: 1px solid #fbd5dc !important;
}

/**** Services Icon back! ****/
a.service {
	font: bold 10px Tahoma !important;
}

