css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("techcrunch.com"), 
domain("crunchgear.com"),
domain("mobilecrunch.com"),
domain("techcrunchit.com")
{
  /* remove the ad column */
  #columns #col2, #post_unit_medrec, #site_supporters, #bugs, .sponsor_units, .excerpt_unit, .third_column_banner, #header_leaderboard, #leaderboard, #sponsors, #ads, #sidebar, #sidebars, #vertical_ads {
    display:none !important;
  }
  .post_unit, #header_features, #announcement{
    display: none !important;
  }
  /* enlarge the content column */
    #col1{
    width:90% !important;
    margin-left: 5% !important;
    padding: 20px 0 10px;
  }
  #top{
    width: 802px !important;
  }
  #container, #footer{
    width: 800px !important;
  }
  #header_right{
    width: 218px !important;
  }
  #header_right{
    text-indent: -5000px;
  }
  #header_subscribe_label{
    display: none !important;
  }
  #columns{
    background: transparent !important;
    border: none !important;
  }
  .excerpt, .post{
    margin-bottom: 20px !important;
  }
  .entry{
    font-size: 11pt !important;
  }
  .cbw{
    display: none;
  }
  .navigation, .neighbor_posts{
    margin: 10px 0 0 !important;
    border: none !important;
  }
  #comments_area, #addcomment, #trackbacks_area{
    display: none;
  }
}
/* Fix for the top navagation on Techcrunch only */
@-moz-document domain("www.techcrunch.com"){
#header_right{
    text-indent: 0px !important;
 }
}
/* Little Fixes for the top bar that are region specific */
@-moz-document domain("jp.techcrunch.com"){
#q{
    width: 100px !important;
 }
}
@-moz-document domain("fr.techcrunch.com"){
#top_left li{
    margin: 0 12px 0 0 !important;
 }
}
