yt-icon.ytd-topbar-logo-renderer {/*logo*/
    animation-name: logoyad;
	filter: none;
    animation-duration: 0.8s;
    animation-iteration-count: 1;
	fill-opacity: 0;
    background-image: url("/*[[logoniyad]]*/");
    background-position: center;
    background-size: 100%;
    background-repeat: no-repeat;
    width: 100%;
    height: 50px;
    max-width: 100px;
    max-height: 100px;
		
}


	@keyframes logoyad {
   0% {transform: scalex(0)}
  25% {transform: scaleY(0.1)}
  50% {transform: scalex(0.7)}
  75% {transform: scaleY(0.15)}
	
}
.ytd-topbar-logo-renderer {
	animation: yadlogo;
	animation-duration:10s;
	animation-iteration-count: infinite;
	animation-fill-mode: both;
	animation-direction: alternate;

}
@keyframes /*[[yadcolorize]]*/ {
	0% {filter: hue-rotate(0deg);}
       100% {filter: hue-rotate(360deg) saturate(6.1);}
}
.ytd-topbar-logo-renderer:hover {
	animation-name: rubberBand;
  animation-duration: 1s;
  animation-delay: 10s;
  animation-fill-mode: both;
  animation-iteration-count: 5;
}
@keyframes yadlogo2 {
  0%{
    -webkit-transform: scaleY(2);
    transform: scaleY(2);
    filter: blur(4px);
    opacity: 0;
  }
  100%{
    -webkit-transform: scaleY(1);
    transform: scaleY(1);
    filter: blur(0);
    opacity: 1;
  }
}
ytd-topbar-logo-renderer:not(:active) {
  animation: yadlogo2;
animation-duration: 1s;
  animation-timing-function: ease;
  animation-iteration-count: 1;
  animation-direction: normal;
  animation-fill-mode: none;
  }
  @keyframes rubberBand {
  0% {
  transform: scale3d(1, 1, 1);
  }
  30% {
  transform: scale3d(1.25, 0.75, 1);
  }
  40% {
  transform: scale3d(0.75, 1.25, 1);
  }
  50% {
  transform: scale3d(1.15, 0.85, 1);
  }
  65% {
  transform: scale3d(.95, 1.05, 1);
  }
  75% {
  transform: scale3d(1.05, .95, 1);
  }
  100% {
  transform: scale3d(1, 1, 1);
  }
  }

