css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anorakforum.com") {

body, #content, table.forums tr td, div.stats, table tr td.inv, table tr td.lp, table tr td{
background:#253960 !important;
color:#eee !important;
}

body {
margin: 0px 0px !important;
border-color: #253960 !important;
}

a, p {
color:#DACA7D !important;
background:#253960 !important;
text-decoration:underline !important;
}

#menubar {
padding: 0px 0px !important;
border-width: 10px 25px !important;
border-color: #0F0433 !important;
}

#datebar {
padding: 0px 0px !important;
border-width: 10px !important;
border-color: #0F0433 !important;
}

a:focus, a:hover {
color:#3366FF !important;}

pre, textarea {
   color: #DCDCDC !important;
   background: #010125 !important;
   border-left-color: #707070 !important;
   border-right-color: #707070 !important;
   border-top-color: #707070 !important;
   border-bottom-color: #707070 !important;
}

input:not([type="button"]):not([type="checkbox"]), 

select {
    color: #DCDCDC !important;
    background: #181818 !important;
    border-left-color: #707070 !important;
    border-right-color: #707070 !important;
    border-top-color: #707070 !important;
    border-bottom-color: #707070 !important;

} 


h1, h2, h3, h4, h5, h6 {color:#fff !important;}

#header, pre, code {background:#0033FF !important;}

tr.by-author td, .author, .post .author a {background:#DACA7b !important; color:#333 !important;}

.quotecontent {background:#8F85FF !important;}

.post .author a:hover {color:#3366FF !important;}

a#uservoice-feedback-tab {display:none !important;}

form.search input {background:#3366FF !important; border-color:#fff !important; border-bottom-width:1px !important; color:#fff !important; }

form.search input:hover {background:#111 !important; border-color:#33CCFF !important;}

#content img, #content pre {max-width: 40% !important;
overflow-x: auto !important;}

pre.sh_sourceCode, pre.sh_sourceCode .sh_comment {background:#253960 !important; color:#DACA7D !important;}

pre.sh_sourceCode .sh_url {color:#3366ff !important;}

.author a {background:#DACA7D !important; color:#111 !important;}

}
