.landingpage {
    background: url(https://i.ibb.co/B4PmrXp/momio-4k-screen.png) center top no-repeat #e5f1fd;
    background-size: cover;
}

html, body {
    width: auto;
    background: url(https://i.ibb.co/zGPKSLY/momio-login-4k-screen.png) top center no-repeat #badef6;
    background-size: cover;
}

.landingpage.sleeping {
    background: url(https://i.ibb.co/Jdzjmbb/momio-sleep-hd.png) top center no-repeat #686ab7;
    background-size: cover;
}

#waiper_content.waiper_bg_sky {
    background: url(https://i.ibb.co/gj9n3k9/momio-bg-clouds.png) center top repeat-x #d8effd;
    background-size: cover;
}
}
