@-moz-document 
/* symoniri (symoniri@gmail.com OR blogger.fa@gmail.com) */

url-prefix(http://friendfeed.com/),
url-prefix(http://friendfeed.com/iphone/),
url-prefix(http://friendfeed.com/settings/notifications) {

/* Global */

.popupmenu .item, .entry .comments, .entry .commentform textarea, .commentform input, .commentform, .entry .body .info, .entry .body .info a, .entry .body .info a:hover, .entry .body .info a:visited, .entry .body .likes, .entry .body .likes a, .entry .body .likes a:hover, .entry .body .likes a:visited, .entry .private .info, .info, likes, td.stats, div.ac_results, .ac_results ul li, #sidebar .box .box-body .section ul li, #sidebar .box .box-body ul li, .subbar, .subbar a, td.request .message, td.request .processed, .footer, a.footer, .edit a.l_choosefavorites, #popup, div.actions input {
font: normal 11px Tahoma !important;
}

.textbox textarea.title, #page .box-body, button.l_subscribe, td.tobody ul.l_tolist, td.tobody ul.l_tolist li, .header .body h1 a.l_editprofile, tr.progress1 td.message, .expandcomment a.l_expandcomments, .entry .clusterlink, .textbox textarea.edittitle, #footer, #footer select, #footer option {
font: normal 12px Tahoma !important;
}

.header .body h1, .entry .title .name, .sharebox, .box-body h2 {
font: bold 12pt Arial !important;
}

#realtimectl a.l_realtimepause {}

/* Entry */

.textbox textarea.title, .textbox textarea.edittitle, div.commentform .text textarea {
line-height: 18px !important;
}

div#feed {
margin-top: 5px !important;
}
.entry {
border: 2px solid #93baeb !important;
-moz-border-radius: 5px !important;
margin: 5px 0 !important;
padding: 5px !important;
}
.foaf {
display: none !important;
}
.entry .title .text, .entry .body .info, .entry .body .likes, .entry .like, .comment {
font-family: Tahoma !important;
}
.entry .title .text {
color:#111 !important;
line-height: 18px !important;
text-align: justify !important;
font-size: 12px !important;
}
.entry .body .info, .entry .private .info, .entry .body .likes {
margin: 5px 0 !important;
}

.likes span.lbody {
line-height: 18px !important;
}

/* Comments */
.comment {
font-size: 11px !important;
background-position: right 4px !important;
padding: 2px !important;
border: 1px solid #e5e5e5 !important;
-moz-border-radius: 5px !important;
line-height: 1.5em !important;
}
.entry .comment .content {
font-size:11px !important;
margin:0 10px 0 0 !important;
}
.entry .comment.friend {
background-color:#f5f5f5 !important;
padding:2px !important;
border:1px solid #e5e5e5 !important;
-moz-border-radius:5px;
}
.entry .comment.owner {
background-color:#fffbef !important;
padding:2px !important;
border:1px solid #f0eacb !important;
-moz-border-radius:5px;
}

.entry .comments, .entry .commentform textarea {
color: #222 !important
}

/* SideBar */
#profile td.body a.l_editprofile, #profile td.body {
font: normal 11px Tahoma !important;
}
#profile td.body .name, #profile td.body .name a {
font: bold 16px Arial !important;
}

