css:
@-moz-document domain("www.nitrotype.com") {
body
  {
    background-color: #0a75ec;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://cdn.discordapp.com/attachments/727592912728359054/772124846112374784/Untitled_design_2_copy.png");
    background: fixed;
  }

  html
  {
    background: fixed url("https://cdn.discordapp.com/attachments/727592912728359054/772124846112374784/Untitled_design_2_copy.png") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
  }

 .bg--gradient {
    background: fixed;
}

  .btn--primary
  {
    background: #0c0c0c;
    color: #ffffff;
  }

  .theme--pDefault .profile-title
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }
  .tc-t {
    color: #ffffff;
  }

  .theme--pDefault .profile-body
  {
    color: #FFFFFF;
  }
}

@-moz-document domain("nitrotype.com") {
.theme--pDefault .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/727592912728359054/772146140229271582/Skittles_Typer_4.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault .profile-bgHead
{
    background: url("https://cdn.discordapp.com/attachments/727592912728359054/772146140229271582/Skittles_Typer_4.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}

.theme--pDefault.is-gold .profile-bg
{
    background: url("https://cdn.discordapp.com/attachments/727592912728359054/772146140229271582/Skittles_Typer_4.png") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #fff 100%);
    color: #d3d3d3;
    background-size: 100%
}
.card--a
{
    background-color: #fff0;
    color: #fff;
}

.dash-copy
  {
    color: #039dfd;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }
.dash-copyContainer
  {
      background: #2d2e2e;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #00a2ff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }

  .bg--gradient {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/727592912728359054/772146140229271582/Skittles_Typer_4.png") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/727592912728359054/772146140229271582/Skittles_Typer_4.png") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/727592912728359054/772146140229271582/Skittles_Typer_4.png") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
}
