css:
@-moz-document domain("washingtonpost.com") {
.italic[data-sc-c="headerlogo"],
.masthead-tagline-decoration {
    font-size: 0;
}

.italic[data-sc-c="headerlogo"]::before,
.masthead-tagline-decoration::after {
    content: "Enemy of the People";
}

.italic[data-sc-c="headerlogo"]::before {
    font-size: 13.5px;
}

.masthead-tagline-decoration::after {
    font-size: 20px;
}

.masthead-tagline-decoration {
    height: 30px;
    width: 220px;
    background: white;
}
}
