css:
@-moz-document domain("bbs.hupu.com") {

body{
    background: #ddd;
}

body, button, input, select, textarea,li  {
    font-family: "Microsoft YaHei", PingFangSC-Regular, STHeiti, "黑体";
    font-size: 14px;
}

/* top nav */
#hp-topbarNav{
    margin: 4px 0 0 0;
    background: none;
    border: none;
}

.hp-logo{
    display: none;
}
.hp-mainNav-B{
    margin: 0 !important;
    opacity: .5;
}
.hp-mainNav-B:hover{
    opacity: 1;
}

.hp-threeNav{
    border: none;
}
.hp-threeNav .hp-threeNav-bd .hp-threeNav-item li a{
    background: none;
    border: none;
    border-radius: 8px;
}
.hp-threeNav .hp-search .it-search:not(:focus){
    width: 80px;
}


/* post list */
body .bbs-content{
    border: none;
    background: none;
}
body .bbs-content #container{
    background: none;
}
body .bbs-content #container .container_padd{
    background: none;
    border:none;
}
body .bbs-content #container .container_padd .bbsHotPit{
    padding: 8px;
    background: #fff;
    border-radius: 8px;
}

.bbsHotPit .bbs_head .left{
    background: none !important;
}
body .bbs-content #container .container_padd .bbsHotPit h1{
    color: #a92229; 
}

.bbsHotPit .show-list .show-tit{
    display: none;
}

.bbsHotPit .show-list .for-list li{
    padding: 4px;
    border-bottom: none;   
}
.showPlate .bbs_banner li a:not(:hover){
    background: none;
}

.bbsHotPit .show-list .for-list li{
    font-size: 100%;
}
.bbsHotPit .show-list .for-list li .titlelink {
    font-size: 100%;
}

.bbsHotPit .show-list .for-list li .titlelink a:visited{
    color: #609 !important;
}

/* post view */
.details #container{
    background: none;
    border: none;
}
.details #container #container_nopadd {
    border: none;
    background: none;
}

.details #container #container_nopadd #sub_line #t_main{
    background: #fff;
    border-radius: 8px;
}

#sub_sidebar{
    background: none;
}
.sidebar-ad-C .shihuo_photo_news{
    border: none;
    opacity: .5;
}
.sidebar-ad-C .shihuo_photo_news:hover{
    opacity: 1;
}

.feedhome h3{
    background: none;
    border: none;
    border-bottom: 1px solid rgba(0,0,0,.2);
}

.floor{
    margin: 0;
    padding: 8px 0;
    background: #fff;
    border: none;
}

.floor .user{
    padding: 0;
}
.floor .user .headpic img{
    border: none;
    border-radius: 100%;
}

.floor .user .badge{
    padding: 2px;
    text-align: center;
}
.floor .user .badge img{
    width: 32px;
    height: 32px;
    border-radius: 4px;
    opacity: .3;
}
.floor .user .badge:hover img{
    opacity: 1;
}

.floor-hide{
    margin: 8px;
    padding: 12px;
    height: auto;
    line-height: normal;
    color: #600;
    border: 1px solid rgba(99,0,0,0.5);
    border-radius: 28px;
    cursor: pointer;
    opacity: .5;
}
.floor-hide:hover{
    opacity: 1;
    background: rgba(199,0,0,0.1);
}

.floor_box {
    margin-left: 56px;
    background: #fff;
    line-height: 180%;
}
#tpc .floor_box{
    margin-left: 56px;
}

.floor_box .author .left a.u{
    font-weight: bold;
}

#container .left {
    white-space: nowrap;
}
.floor_box .author{
    border-bottom: 1px solid #ccc;
}
.floor_box .author .left .post-owner {
    color: #fff;
    background: #f60;
}

.floor_box .case img{
    border: 1px solid rgba(0,0,0,0.1);
}
.floor .user .headpic img{
    padding: 0;    
}


#code, .quote, .floor_box blockquote{
    background: rgba(0,0,0,0.05);
    border: 1px solid rgba(0,0,0,0.1);
    border-radius: 8px;
}

.w_reply{
    background: #f9f6ee;
    border: 1px solid #fc0;
    border-radius: 8px;
}
.w_reply h5{
    margin: 8px;
    color: #000;
    border: none;
}
.w_reply .floor_box, .l_w_reply .floor_box{
    border: none;
}
.w_reply .floor .author, .l_w_reply .floor .author{
    border-bottom: 1px solid #ccc;
}
.w_reply .floor, .l_w_reply .floor{
    background: none;
}
#code, .quote, .floor_box blockquote{
    background: rgba(0,0,0,0.05);
    border: 1px solid rgba(0,0,0,0.1);
    border-radius: 8px;
    opacity: 0.7;
}

.floor_box .liangShare{
    display: none;
}

/* reply box */
#re #re_top, #re #re_bottom{
    background: none;
}

#re #re_box{
    background: rgba(0,0,0,0.05);
    border: none;
    border-radius: 12px;
}

#re #re_box .left .headpic img{
    border-radius: 100%;
}

#re #re_box .j_enter{
    display: block;
}

.ad_reply{
    border: none;
    opacity: .5;
}
.ad_reply:hover{
    opacity: 1;
}



}
