css:
@-moz-document domain("live.nicovideo.jp") {
@media (max-width: /*[[max-width]]*/) {
html html,
/* ヘッダー*/
#common-header,
[class*="___page-header-area___"],
/* プレミアム会員宣伝 */
[class*="___banner-panel___"],
/*[[comment]]*/
/*[[information]]*/
/*[[tab-area]]*/
/*[[nicoad]]*/
html html {
    display: none !important; }

[class*="___player-area___"] {
    padding: 0 !important; }

[class*="___player-section___"] {
    margin: 0 !important; }

[class*="broadcast-participation-area"] {
    border: none !important; }

/* 親 */
[class*="___leo-player___"] {
    display: flex !important;
    flex-direction: column !important;
    height: calc(100vh - 17px) !important;
    max-width: calc(100vw - 17px) !important; }

/* 子 */
[class*="___player-display___"],
[class*="player-status"],
[class*="___table___"] {
    width: auto !important;
    min-width: 0 !important; }

[class*="___ng-delete-form___"] {
    overflow: auto !important; }

.___watch-page___[data-layout-mode="liquid"] [class*="___leo-player___"]:not([data-fullscreen-layout]) [class*="___player-display-screen___"],
[class*="___player-status___"],
[class*="player-status-panel"] {
    flex-grow: 1; }

/*[[color]]*/
}
}

style_setting_options.value:
/* コメント欄色変更 */
[class*="___table-row___"],
[class*="___player-status___"],
[class*="___program-title___"],
[class*="___tab___"],
[class*="___tab-area___"] {
    background: #1e1e1e !important;
    color: #e0e0e0; }
    
[class*="___broadcast-participation-area___"] {
    background: #1e1e1e !important; }

[class*="___program-card___"]:hover,
[class*="___program-card___"]:hover [class*="___program-title___"] {
    background: #383838 !important; }

[class*="___player-status___"],
[class*="___tab-area___"],
[class*="___program-statistics-menu___"] {
    border-color: #383838 !important; }

[class*="___ng-setting-popup-button___"] > svg {
    fill: #e0e0e0; }
