css:
@-moz-document domain("platform.twitter.com") {
.count-o {
	background: #000;
}
#count:focus, #count:hover {
	color: #ccc;
}
#count {
    color: #a9c3d5;
}
.count-o u {
    border-right-color: #000;
}
.count-o i, .count-o u {
    border-right-color: #96acbb;
}
}

@-moz-document domain("fool.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background: linear-gradient(180deg, #a124e3 0%, #d56429 100%)
        }

.gdpr-modal-content, .privacy-content fieldset, .gdpr-modal-content fieldset, header.navigation .navigation-wrapper, .fool-tophat-container, #article-page .full_article, .header-search-form-wrapper, #investing-news-section, .hp-services-volatility, .home-page .across-the-web-section, [dir] body {
	background: #000;
}
[src='https://g.foolcdn.com/misc-assets/foolcapwithtext.svg'], [src='https://g.foolcdn.com/misc-assets/logo-tmf-primary-2-purple-royalpurple.svg'], [src='https://g.foolcdn.com/misc-assets/the-ascent-on-white.svg'], [src='https://g.foolcdn.com/image/?url=https%3A%2F%2Fg.foolcdn.com%2Fart%2Fcompanylogos%2Fmark%2FAAPL.png&w=64&h=64&op=resize'], [src='https://g.foolcdn.com/misc-assets/logo-tmf-primary-8-purple-blue.svg'], [src='https://g.foolcdn.com/misc-assets/logo-tmf-primary-4-cyan-green.svg'] {
	filter: invert(100);
}
.privacy-content h1, .gdpr-modal-content h1 {
	color: #ddd;
}
.privacy-content .intro, .gdpr-modal-content .intro {
    border-color: #002f00;
    background-color: #181c18;
}
.privacy-content p, .gdpr-modal-content p, .privacy-content label, .gdpr-modal-content label {
	color: #fff;
}
body {
	background: #121111;
}
header h1, h1, .article-body .article-content h2, .article-body .article-disclosure a, .stocks-info-item .td.name small {
	color: #c7ced5;
}
body {
    color: #93989f;
}
.article-body .article-content {
    color: #c2c8dd;
}
.article-body .article-content a:link, .article-body .article-content a:visited {
    color: #7200ec;
}
.author-tagline .author-bio-popup {
    background-color: #000;
    box-shadow: 0 1px 5px rgba(255, 255, 255, .3);
}
.special-message {
    border-color: #575859;
}
.main-menu .main-menu-item>a:hover, .main-menu .main-menu-item>a.hover, .main-menu .main-menu-item>a.open, .main-menu .main-menu-item>a.focus  {
	background: #000;
	box-shadow: 0 0 1px #49494a;
}
.main-menu .main-menu-item>a, .wayfinder h2, .service-panel-return .item.S_P, .content-container, .content-block article .text {
    color: #a3a9b0;
}
.mega-menu-wrapper {
    background-color: #000;
    box-shadow: 5px 5px 5px rgba(255, 255, 255, .4);
}
hr, ul.read-more-ul li {
	border-color: #181818;
}
.market-performance-card {
	background: #000;
	border-color: #0f0101;
}
.market-table .market-table-cta, .market-table .item .Fool100 {
	border-color: #353434;
}
input {
	background: #000;
	color: #fff;
	border-color: #111;
}
#fool-search {
	border-color: #575859;
	box-shadow: 0 0 0 #c3cadf inset;
}
#header-search-submit {
	background: #575859;
	color: #000;
}
#header-search-submit .fa-svg-icon { 
	fill: #000;
}
.ticker-input-results .ticker-input-results-result.active {
	background: #121111;
}
.ticker-input-results {
	background: #000;
	box-shadow: 0 0 5px #575859;
}
.ticker-input-results .ticker-input-results-result, ul.hp-trending-articles-list li, .home-page .across-the-web-section h3 {
	border-color: #575859;
}
.item.market-table-graph > img, img.sp-graph {
	filter: invert(100);
}
.home-page .panel-section {
    box-shadow: 0 1px 2px 0 rgba(255, 255, 255, .08);
	border-color: #181818;
}
.home-page h2, .content-container hr {
	border-color: #49494a;
}
#how-help-section, .service-panel, .content-block {
	background: #000;
}
#how-help-section .how-help-grid .grid-item {
    font-family: -apple-system,BlinkMacSystemFont,"Segoe UI","Oxygen","Ubuntu","Cantarell","Fira Sans","Droid Sans","Helvetica Neue",sans-serif;
    background: linear-gradient(#121111,transparent);
    border-color: #575859;
    color: #485CC7;
}
.hp-service-tab {
	background-color: #121111;
	border-color: #49494a;
}
.active .hp-services-volatility-graph-group-item {
	background: #575859;
}
.hp-services-volatility-graph-group-item {
	background: #121111;
}
.hp-service-tab:after {
	border-color: #000;
}
.service-flex-cols.top, .service-panel {
	border-color: #232322;
}
.service-panel-features .item header {
    color: #c3c9d9;
}
.service-flex-cols.advantages .col.graph, .service-flex-cols.advantages .col.logo {
	background: #121111;
	color: #93989f;
}
.service-panel-image {
	background: #121111;
}
.service-panel-return .item:first-child:after {
	background: #353434;
}
.home-page .around-the-web-sidebar .more-from-the-motley-fool {
    background-color: #000;
    box-shadow: 0 0 2px -1px rgba(255, 255, 255, .12), 0 2px 2px rgba(255, 255, 255, .24);
    border-color: #575859;
}
.home-page .around-the-web-sidebar .more-from-the-motley-fool h4 .fa-svg-icon {
	fill: #fff;
}
.sister-companies p, .content-block .content-block-label, .content-block article .story-date-author, .content-block article .author-byline {
    color: #9fa2a6;
}
.related-tickers .ticker-row a.quote-image {
	background-color: #121111;
	color: #a3a9b0;
}
.waves__wave {
	fill: #000;
}
[dir] .layout {
	background: #000;
}
[dir] .custom-block__link, [dir] .block-list-item__link {
    background-color: #000;
    box-shadow: 0 0 15px 5px rgba(108, 105, 172, .08);
}
.custom-block__title {
	color: #eee;
}
.custom-block__description {
	color: #676767;
}
[dir] .search-box--hero-unit #query {
	background: #000;
	border-color: #000;
}
[dir] .search-box--hero-unit #query:focus {
	background: #000;
}
input[type="search"] {
	color: #999;
}
.waves__wave--footer-primary {
    fill: #504d7f;
}
.waves__wave-footer-with-opacity {
    fill: #504d7f;
}
.stocks-info-table {
	background: #000;
	border-color: #49494a;
}
.main-menu .main-menu-item.dropdown>a .fa-svg-icon {
	fill: #fff;
}
#services h1 {
    color: #c3c8d9;
}
.service-panel-image > img, [src='https://g.foolcdn.com/misc-assets/logo-tmf-primary-6-red-purple.svg'], [src='https://g.foolcdn.com/misc-assets/logo-tmf-primary-1-magenta-purple.svg'] {
	filter: invert(100);
}
.section__title-link {
	color: #999;
}
}
