css:
@-moz-document domain("frondtech.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #111;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #111;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #777;
        }

body, h1, .entry-title a, .entry-content h1, h2, .entry-content h2, h3, .entry-content h3, h4, .entry-content h4, h5, .entry-content h5, h6, .entry-content h6, .wc-block-grid__product-title, .main-header-menu .menu-link, .ast-header-custom-item a, .widget-title, a:hover, a:focus, .entry-meta a:hover, .entry-meta a:hover *, .entry-meta a:focus, .entry-meta a:focus * {
	color: #cacaca;
}
body, .ast-separate-container, .ast-below-header, .ast-below-header-menu .sub-menu {
	background: #070402;
}
.ast-separate-container .ast-article-single, .ast-separate-container .comment-respond, .ast-separate-container .ast-comment-list li, .ast-separate-container .ast-woocommerce-container, .ast-separate-container .error-404, .ast-separate-container .no-results, .single.ast-separate-container .ast-author-meta, .ast-separate-container .related-posts-title-wrapper, .ast-separate-container.ast-two-container #secondary .widget, .ast-separate-container .comments-count-wrapper, .ast-box-layout.ast-plain-container .site-content, .ast-padded-layout.ast-plain-container .site-content, .main-header-bar, .ast-separate-container .blog-layout-1, .ast-separate-container .blog-layout-2, .ast-separate-container .blog-layout-3, .main-header-bar .ast-search-menu-icon .search-form {
	background: #000;
}
input[type=email], input[type=number], input[type=password], input[type=reset], input[type=search], input[type=tel], input[type=text], input[type=url], select, textarea {
	background: #0a0a0a !important;
	border-color: #1a1a1a !important;
	color: #999 !important;
}
hr {
	background: #333;
}
.site-title a, .site-title a:focus, .site-title a:hover, .site-title a:visited {
	color: #ddd;
}
.ast-search-menu-icon .search-form {
	border-color: #171717;
}
.ast-search-menu-icon .search-submit {
	color: #858585;
}
.lwptoc-light .lwptoc_i {
	background: #000;
	color: #ccc;
}
}
