css:
@-moz-document url-prefix("https://news.ycombinator.com/") {
:root {
    --body-color: #242323;
    --accent-color:#282727; 
    --light-text: #cac9c9;
    --dark-text: #a8a6a6;
    --header: #424141;
    --link-color: #f28c49;
    --link-bg: #3e2a1c;
}

body {
    background-color: var(--body-color);
    color: var(--light-text);
}

input, textarea {
    background-color: var(--body-color);
    border: 1px solid black;
    color: var(--light-text);
}

p {
    color: var(--light-text);
}

b {
    color: var(--light-text);
}

input {
    border-color: var(--light-text);
    border-radius: 4px;
}

td {
    color: var(--light-text);
}

td[bgcolor="#fafaf0"] {
    background-color: var(--body-color);
}

p a {
   color: var(--link-color) !important;     
}

a:link {
    color: var(--light-text);
}

a:hover {
    color: var(--link-color) !important;
}

a:visited {
    color: var(--dark-text);
}

.itemlist {
    color: white;
}

.c00, .c00 a:link, .c88, .c88 a:link, .c88 a:visited {
    color: var(--light-text);
}

.title {
    color: var(--light-text);
}


.spacer {
    height: 16px !important;
}

.admin td {
    color: var(--light-text);
}

.pagetop {
    color: var(--light-text);
}

.pagetop a:visited {
    color: var(--light-text);
}

#hnmain {
    background-color: var(--accent-color);
}

/* Header bar */
#hnmain td[bgcolor="#ff6600"] {
    background-color: var(--header);
    padding: 10px;
}

/* Alert bar */
#hnmain td[bgcolor="#ffffaa"] {
    border: 1px solid var(--link-color);
    border-radius: 5px;
    margin-bottom: 5px;
    background-color: var(--link-bg);
}
}
