 * {
    font-family: Exo !important;
}
body {
    font-family: 'Exo', sans-serif;
}
body.main-page {
    font-family: 'Exo', sans-serif;
    background: #191919;
}
.site-header {
    border-bottom: 1px solid #f47521;
    background: #191919;
}
.header-menubar li a {
    color: #ffffff;
}
#template_container.template-container {
    background: #191919;
}
.new-template-body {
    color: #f47521;
}
#template_body.old-template-body {
    background-color: #191919;
}
.guestbook-sort-bar {
    border-top: 1px solid #f47521;
    padding-top: 3px;
    background-color: #272727;
}
.guestbook-list {
    border-top: 1px solid #f47521;
}
#footer_menu {
    background-color: #333;
}
main_content_white, .white-wrapper {
    padding: 12px;
    background: #272727;
}
.new-template-body a.block-link, .old-template-body a.block-link, a.block-link {
    color: #ffffff;
}
.landscape-grid .wrapper:hover {
    background: #272727;
}
textarea {
    color: #f47521;
    background: #272727;
    border: 1px solid #3d3d3d !important;
}
.default-button, a.default-button, input.default-button, span.default-button {
    color: #ffffff;
    border: 1px solid #f47521;
    text-shadow: 0 1px 3px #eee;
    background: #ececec;
    background: linear-gradient(to bottom, #3d3d3d 15%, #272727 100%);
}
.comments .boxtitle {
    border-bottom: 2px solid #f47521;
}
.new-template-body h3 {
    border-top: 1px solid #f47521;
}
.welcome-block h1, .welcome-title-list h1 {
    font-size: 20px;
    border-bottom: 1px solid #f47521;
    color: #fff;
}
.welcome-block h1 a, .welcome-title-list h1 a {
    color: #fff;
}
.welcome-crnews-title, .welcome-news-title {
    color: #888;
}
.welcome-crnews-contents > p {
    color: #ccc
}
.welcome-crnews-item, .welcome-news-item {
    border-bottom: 1px solid #f47521;
}
h3 {
    color: #fff
}
#sidebar {
    border-bottom: 1px solid #f47521;
}
#welcome_left {
    border-right: 1px solid #f47521;
}
.welcome-forum-category a {
    color: #fff;
}
.welcome-pulse-box a {
    color: #888;
}
.welcome-forum-category {
    border-bottom: 1px solid #f47521;
}
.welcome-pulse-box a:hover {
    background-color: #333;
}
.welcome-countdown-day {
    border-bottom: 1px solid #f47521;
}
.welcome-countdown-day {
    color: #fff;
}
.welcome-countdown-items a {
    color: #888;
}
.welcome-countdown-items a:hover {
    background-color: #333;
}
.welcome-simulcasts .welcome-simulcasts-collection a {
    color: #888;
}
.welcome-simulcasts .welcome-simulcasts-episode {
    color: #888;
}
.welcome-simulcasts .welcome-simulcasts-episode:hover {
    background-color: #333;
}
.welcome-latest-videos .series {
    color: #888;
}
.welcome-crnews-mug img, .welcome-news-mug img {
    border: 1px solid #f47521;
}
.show-all-simulcasts-button {
    color: #ffffff;
    border: 1px solid #f47521;
    text-shadow: 0 1px 3px #eee;
    background: #ececec;
    background: linear-gradient(to bottom, #3d3d3d 15%, #272727 100%);
}
#tabs {
    background: #333;
}
.new-template-body a.block-link, .old-template-body a.block-link, a.block-link {
    color: #888;
}
.portrait-element {
    background: #333;
}
img {
    border: 1px solid #df6300;
}
