css:
@-moz-document url("https://vk.link/megu.arhimag") {
/*Author: vk.com/darsox*/
/*Style: Стиль созданы для сайт-группы бота: vk.com/megu.arhimag*/


[dir] .Header--scrolled {
    background-color: #212121;
}
.Header {
    padding: 12px 16px;
	background-color: #212121;
}

[dir] .Header__groupName--dark {
    color: #f94444;
}
.Header__groupName--light {
    color: #f90000;
}

.Nav--light .Nav__link--active, .Nav--light .Nav__link:hover {
    opacity: 1;
	color: #f90000;
}

.Advantages--desktop .Advantages__item {
    color: #fff;
}

[dir] .Review {
    background-color: #212121;
}

[dir] .Advantages--desktop {
    border-bottom-width: 0;
    padding-top: 1px;
    padding-bottom: 100px;
}

[dir] .MicroLanding__content {
    margin-top: 0px;
    padding-top: 50px;
    margin-bottom: 0px;
    background-color: #0a0a0a;
}

[dir] .Footer--emptyInfo {
    border-top: 1px solid #ffd87b;
}

[dir] .Block__title {
    margin: 0 auto;
    padding: 0;
    text-align: center;
	color: #ffeb00;
}

.ProductItem__name {
    color: #ff0707;
}

.Link--default {
    color: #ffeb00;
}

.Review__link:hover {
    color: #ffffff;
}

.Review__link {
    color: #ff2b2b;
}

.Review__date {
    color: #ffffff;
}

[dir] .Link--button {
    background: #403f3f;
}

.Link--button {
    color: #ffeb00;
}



[dir] .FooterLinks {
    padding: 16px 120px;
	display: None
}
[dir] .Footer__content--emptyInfo {
    padding-top: 80px;
    padding-bottom: 80px;
	background: #0a0a0a;
}

[dir] .Footer__content {
    padding: 56px 0px;
    border: 0px solid var(--block_border);
}
.Contact {
    color: #ff0000;
}

.Footer__groupName {
    color: #ffd400;
}

.Footer__groupMembers {
    display: inline-block;
    font-size: 14px;
    color: #fffa00;
}

.Footer__groupStatus {
    color: #ffffff;
}

[dir] .Review__textWrap {
    margin-bottom: 8px;
	color: #ffffff;
}

.ProductItem__price {
    color: #ffffff;
}

[dir=ltr] .Review:last-child {
    margin-right: 0;
	background-color: #212121;
}

.Header__groupMembers--dark {
    color: #ffffff;
}

.Nav--dark .Nav__link--active, .Nav--dark .Nav__link:hover {
    color: #ff0000;
}
.Nav__link--dark {
    color: #ffffff;
}
}
