/* Background filtering */
body.background-image {
    background-image: none;
    position: relative;
}
body::before {
    content: "";
    position: absolute;
    z-index: -1;
    width: 100%;
    height: 100%;
    background-image: url(https://vignette.wikia.nocookie.net/warframe/images/5/50/Wiki-background/revision/latest?cb=20190402071843);
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-position: 50% 40px;
    filter: invert(0.868) blur(6.82px) grayscale(33%);
}

/* Various tables */
table {
    color: #cacaca; /* sadly the wiki has a lot of inline styled elements, including tables, which makes  !important;  spread like the plauge */
}
table:not([style*='background']) tr:nth-child(odd) td{
    background-color: #575658;
}
table:not([style*='background']) tr:nth-child(even) td{
