css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.portal.uni-erlangen.de") {
  #seite {
     margin: 0 !important;
     padding: 0 !important;
     border: none !important;
  }
  #kopf {
     background: none !important;
     padding: 0 !important;
     margin: 0 !important;
  }
  #kopf #logo {
      height: 22px !important;      
  }
  #kopf #logo img {
      width: 20px !important;
      height: 20px !important;
   }
  #kopf #logo p {
      font-size: 0.7em !important;
      top: 0 !important;
      left: 0 !important;
      width: 265px !important;
      padding: 2px 1px 1px 1px !important;;
   }
  #kopf #logo p br {
     display: none !important;
  }
  #kopf #breadcrumb {
    display: none !important;
  
     top: 30px !important;
     background: transparent !important;
  }
  #kopf #breadcrumb h2 {
     display: none !important;
  }
   #kopf #breadcrumb p{
     font-size: 0.6em !important;
  }
  #kopf #suche {
      position: absolute !important;
      top: 10px !important;
      right: 5px !important;
      left: auto !important;
      border: 1px solid #999 !important;
      background-color: #eee !important;
      width: auto !important;
      height: auto !important;
      padding: 3px !important;
  } 
  #kopf #suche p {
      display: inline !important;
      font-size: 0.7em !important;
      margin: 0 !important;
   }
  #kopf #suche p:hover {
    font-size: 1.1em !important;
  }
  #kopf #suche p label {
   /*  display: inline !important;
     position: relative !important;
     top: 0 !important;
     left: 0 !important;
     width: auto !important;
     font-size: 0.9em !important;
     float: none !important;  */
  }

 #kopf #suche p input { 
    position: relative !important;  
  }
  #kopf #suche input#suchbegriff {        
      width: 20em !important;     
  }
  #kopf #titel {
     top: 0px !important;     
     left: 245px !important;
     width: 200px !important;
    
     height: 22px !important;      
     
   
  }
  #kopf #titel h1 {
     display: inline !important;
     font-size: 0.7em !important;
     color: white !important;  
     padding: 2px 1px 1px 3px !important;;
     font-weight: bold !important;
  }
  #hauptmenu {
     display: none !important;
   }
   #main #menu {
      margin-top: 10px !important;
   }
   div#menu div#bereichsmenu {
      left: 0px !important;
   }
   #content {
     margin-top: 70px !important;
     margin-right: 1px !important;
     padding: 0 !important;
   }
   #content .vcard {
      width: auto !important;
      margin: 0px !important;
      padding: 0px !important;
      display: block !important;
   }
   #content .vcard #photo img.photo {
      margin: 2px !important;
      width: 50px !important;
      height: 50px !important;
      float: left !important;
      border: none !important;
   }
   #content #whois {
       position: relative !important;;
       display: block !important;;
       float: right !important;;
       font-size: 0.8em !important;
       border: 1px solid #999;
       margin: 0px !important;      
   }
   #content #whois ul.whoisstats {
      padding: 0 !important;
   }
   #content #whois ul.whoisstats li {
      border: none !important;
      line-height: 1em !important;
   } 
   #content .vcard  #about {
      width: auto !important;      
      display: inline !important;
      margin: 1em !important;
   }
   #content  .vcard #about p.clear {
    clear: none !important;  
   }

   #content .vcard  #about h2 {
      margin-top: 10px !important;
    }
    #content .vcard  #about h3 {
      display: none !important;
   }
   #activities {
     display: block !important;
     clear: both;
   }
   #kurzinfo {
        position: absolute !important;
        top: 0px !important;
        left: 0px !important;
        width: 99% !important;
   }
  #kurzinfo #faulogo {
      display: none !important;
   }
   #communitymenu {

     position: absolute !important;
     top: 7px !important;
     left: 0px !important;

     display: block;
     margin: 0px !important;
     padding: 0px !important;

     width: 99% !important;

     clear: both !important;
 
   }
   #communitymenu h2 {
      display: none !important;
   }
   #communitymenu ul {
      
        display: inline !important;
        width: 99% !important;
        max-width: 1200px !important;
       position: absolute !important;;
       left: 270px !important;;
   }
   #kurzinfo #communitymenu ul li {
      display: inline !important;
      width: auto !important;
      clear: none !important;
      border-top: 1px solid white;
      border-bottom: 1px solid #ddd !important;;;      
      border-right: 1px solid #ddd !important;;;      
   }
   #kurzinfo #communitymenu ul li:hover {
      border-top: 1px solid #008000 !important;;;
      border-bottom: 1px solid #008000 !important;;;
      border-right: 1px solid #008000 !important;;;      
   }
   #kurzinfo #communitymenu ul li .aktiv {
       background-color:  #AADDAA !important;
       color: black !important;
        border-bottom: 1px solid #008000 !important;;;
   }
   #kurzinfo #communitymenu ul li ul.submenu {
       position: absolute !important;
       top: 24px !important;
       left: 0px !important;
       font-size: 0.8em !important;
       width: 99% !important;
       display: inline !important;
       
   }
   #kurzinfo #communitymenu ul li ul.submenu li a {
      background-color: #AADDAA !important;  
      border-color:  #008000 !important; 
      border-bottom: 1px solid #008000 !important;;;
      border-right: 1px solid #008000 !important;;; 
   }
   #footer p {
        display: none !important;
   }
   #footer p.version  {
     display: block !important;
     position: relative !important;
     left: 1px !important;
     top: 0px !important;
   }
   #footer #footerinfos #tecmenu {
     position: relative !important;
     margin-left: 10px !important;
   }
   div#footer div#footerinfos div#tecmenu ul li span, 
   div#footer div#footerinfos div#tecmenu ul li a, 
   div#footer div#footerinfos div#tecmenu ul li a:link {
      color: #444 !important;
  }
}
