css:
@-moz-document url-prefix("https://www.baidu.com/s") {
#s_tab {
  height: 0;
}
.search_tool {
  float: left;
  padding-left: 18px;
}
#form {
  position: absolute;
  left: 50%;
  transform: translateX(-50%);
}
#head {
  border-bottom: 1px solid #4d4d4d !important;
}
#page {
  border-top: 1px solid #4d4d4d !important;
}
#container {
  margin: 0 auto !important;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.page-inner {
  display: flex;
  justify-content: flex-end;
  padding-right: 10px !important;
}
.search_tool {
  padding-left: 20px;
}
.search_tool_conter.new_search_tool_conter {
  padding-left: 25px;
}
#wrapper_wrapper {
  margin-top: 20px;
}
#content_left > div {
  background-color: #252525;
  padding: 10px 20px;
  border-radius: 5px;
}
#content_left > div:hover {
/*   transform: scale(1.003); */
  transition: all .05s ease-out;
}
/* #content_left > div:hover .c-img-radius-large {
  border-radius: 0 !important;
  transition: all .2s ease-in-out;
} */
.c-row.c-gap-top-small {
  display: flex;
  align-items: center;
}
.bg.s_ipt_wr.quickdelete-wrap {
  border: 1px solid #b5b1b1 !important;
  border-radius: 24px;
  box-shadow: 0 1px 2px rgba(251, 251, 251, .15), -1px 0 2px rgba(251, 251, 251, .15);
}
.bg.s_ipt_wr.quickdelete-wrap:hover,
.bg.s_ipt_wr.quickdelete-wrap:focus{
  text-decoration: none;
  border: 1px solid #b5b1b1 !important;
}
#kw {
  color: #fff;
  z-index: 999;
}

.wrapper_new #form .bdsug-new {
  box-shadow: -1px 2px 2px rgba(251, 251, 251, .15) !important;
  border: 1px solid #b5b1b1 !important;
  border-top: none !important;
}
.wrapper_new #form .bdsug-new ul {
  margin-top: 20px;
  border-top: 1px solid #fff;
}
#form > div {
  background-color: #323232;
  top: 23px !important;
  box-shadow: 2px 2px 2px rgba(168, 164, 164, .15) !important;
}

a {
  color: #fff;
}
em {
  color: #EE7762 !important;
}
.c-abstract em {
  color: #ca6250e3 !important;
}
a em {
  text-decoration: none;
  border-radius: 2px;
  padding: 0;
}
a,
a:hover,
a:hover em{
  text-decoration: none;
}
a:hover,
a:hover > em {
  color: #e8a062 !important;
}
a:visited {
  color: #7d7c7c;
}
.bg.s_btn_wr {
  display: none;
}
#su {
  display: none;
/*   font-size: 14px;
  height: 38px;
  line-height: 38px;
  border-radius: 0 24px 24px 0;
  background-color: #323232;
  border: 1px solid #c4bbbb;
  border-left: none;
  box-shadow: 1px 1px 2px rgba(251, 251, 251, .15); */
}
#su:hover {
  background: #8c8989;
  color: #3c3a3a;
}
.c-border {
  border: 1px solid #535151 !important;
}
#kw_tip {
  z-index: 999;
}
.wrapper_new .s_ipt {
  height: 36px;
}
}

@-moz-document url-prefix("https://www.baidu.com/s") {
/* 主题部分 */
body {
  background-color: #252525;
}
.s_form,
.page-inner {
  background-color: #323232; 
}
.c-abstract,
span > a,
h3 > a,
p,
div,
span,
.op_dict3_linetwo_result {
  color: #C8C8C8 !important;
}
a::selection,
em::selection,
.c-abstract::selection,
span > a::selection,
p::selection,
div::selection,
span::selection,
.op_dict3_linetwo_result::selection{
  background-color: #b9b5b5 !important;
}

input {
  color: #444242;
}
li {
  color: #dfdfdf !important;
}
li b {
  color: #b9b1b1 !important;
}
.wrapper_new .sam_newgrid~#page strong {
  background: #737070 !important;
}
.page-inner > a {
  background: #444242 !important;
}
.n {
  color: #fff !important;
}
.n:hover,
.page-inner a:hover span {
  background: #b9b5b5 !important;
  color: black !important;
}
}

@-moz-document url-prefix("https://www.baidu.com/s") {
/* 隐藏部分 */
#u,
#content_right,
#foot,
.s_tab_inner,
.nums_text,
#result_logo,
#rs,
.f13.c-gap-top-xsmall.se_st_footer,
.result-op.c-container.new-pmd[tpl="recommend_list"],
.c-gap-top-xsmall.c-recommend.recommend-none-border,
.bdsug-feedback-wrap,
.m,
.c-tip-icon.c-icon,
.c-container.aladdin__2AELi.new-pmd.wa-zp-exact-new-inner-top,
.c-container.result[tpl="zp_exact_new"],
.c-gap-top-xsmall.c-recommend.recommend-none-border,
.tab-wrapper,
#header_top_bar,
a[mon="gotoPs=1&rsv_xinwen=1"]{
  display: none;
}
}
