css:
@-moz-document url-prefix(http://www.netvibes.com/)
ul.nv-feedList{
	list-style: none;
}
	.rssItemList li,
	.nv-feedList li{
		margin: 0;
		padding: 1px 0 1px 10px;
		line-height: 1em;
		color: #000;

	}
	.rssItemList li a,
	.nv-feedList li a{
		text-decoration: none;
                font-weight: bolder;
		border-bottom: 1px solid #FFF;
		display: block;
		color: #000;
	}
	.rssItemList li a:visited,
	.nv-feedList li a:visited{
		color: #999;
font-weight: normal;
	}
	.rssItemList li.read a,
	.nv-feedList li.read a{
		color: #999;
font-weight: normal;
	}
	.rssItemList li.unread a,
	.rssItemList li.unread a:visited,
	.nv-feedList li.unread a,
	.nv-feedList li.unread a:visited{
		color: #000;
	}
	.rssItemList li a:hover,
	.nv-feedList li a:hover{
		background: #FFFFE0;
		color: #F60;
		border-bottom: 1px solid #DCDC87;
		text-decoration: none;
		display: block;
	}
