css:
/* i really want this to be global */

#parameter /* это чтобы параметры и навыки были наверху  */
  {
    position: fixed;
    width: 170px;
    top: 30px;
    right: 75px;
    border-radius: 10px;
    text-align: center;
    padding: 3px;               
  }

a#parameters.toggle  /* "параметры и навыки" */
  {
	 text-align: center;
     font-size: 17px;
  } 

#history /* растянутый блок истории */
  {
    width: 100%;
  }

.parameter>tbody>tr>td[style^="width"] {
    border:1px solid transparent!important;
    border-radius: 8px 0px 0px 8px;}
.parameter>tbody>tr>td[style^="width: 150px"],.parameter>tbody>tr>td[style^="width:150px"] {
    border:1px solid transparent!important;
    border-radius: 8px 8px 8px 8px;}
.parameter>tbody>tr>td[style^="background"] {
    border:1px solid transparent!important;
    border-radius: 0px 8px 8px 0px;}
.parameter>tbody>tr>td[style$=" 0px;"],.parameter>tbody>tr>td[style$="0px"] {
    display:none;}
