css:
@-moz-document domain("bbs.nga.cn") {
#toptopics,
#bbs_ads9_add,
#custombg,
#fast_post_c,
#m_pbtntop,
div.td:nth-child(5),
#b_nav,
.small_colored_text_btn.stxt.block_txt_c0.vertmod {
    display: none!important;
}

#m_threads > div:nth-child(1) > span:nth-child(2) > div,
#b_nav > div:nth-child(3),
.null,
#m_posts_c > span > div:nth-child(1) {
    display: none!important;
}

#mainmenu {
    margin-bottom: 1rem
}

.c1 > a {
    font-size: 1.3em!important
}

.c2 {
    font-size: 1.25em
}

.forumbox .c1,
.forumbox .c2,
.forumbox .c3,
.forumbox .c4,
.forumbox .c5,
.forumbox .c6,
.forumbox .contentBlock {
    padding-bottom: 1.2em;
}

a.author.b.nobr {
    font-size: 1.2em
}

.block_txt {
    margin-right: 0.2em;
}

.block_txt_c2 {
    background: #df9746;
}

#postsubject0 {
    padding-left: 0.5em
}

#posteravatar2 {
    max-width: 8em!important
}

.forumbox .postrow .c1 {
    width: 14%;
}

.pc39 > br:nth-child(2) {
    line-height: 1em
}

.forumbox .topicrow .c3,
.forumbox .topicrow .c4 {
    width: 10%;
}

#m_nav {
    position: fixed;
    bottom: 0.5rem;
    left: 0;
    opacity: 0.3;
}
#m_nav:hover {
    opacity: 1;
    z-index: 1000;
}
#m_pbtnbtm {
    position: fixed;
    bottom: 0.5rem;
    right: 0;
    opacity: 0.3;
}
#m_pbtnbtm:hover {
    opacity: 1;
    z-index: 1000;
}
body {
    margin-bottom: 4rem;
}
}
