css:
@-moz-document domain("goodblox.xyz") {
/*
hello this is a mess
enjoy
*/

body {
    background: url(http://web.archive.org/web/20100522111914im_/http://www.roblox.com/images/bg_clouds.jpg) no-repeat scroll center top, #F8FCFF url(http://web.archive.org/web/20100522112159im_/http://www.roblox.com/images/bg_gradient.jpg) repeat-x;
    color: #000;
    background-color: #fff0;
}


#Logo img {
    content: url("https://goodblox.xyz/resources/FrozenSummer.png");
}
#Banner {
    background-image: url("https://goodblox.xyz/resources/banners/ChristmasInJulyBanner.png");
}

#ProfilePane {
    background: #afc3de;
}

a:link,
a:visited,
a:active {
    color: Blue;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point1 {
    background-image: url("https://goodblox.xyz/resources/bullet4.png");
}
#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point2 {
    background-image: url(https://goodblox.xyz/resources/bullet5.png);
}
#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point3 {
    background-image: url(https://goodblox.xyz/resources/bullet6.png);
}
#ForParents img {
    content: url("https://goodblox.xyz/resources/SummerSeal.png");
}

#SplashContainer h2 {
    visibility: hidden;
}
#SplashContainer h2:after {
    content: 'GoodBlox Virtual Frozenworld';
    visibility: visible;
    display: block;
    position: absolute;
    padding: 0px;
    top: 9px;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point1 h3 {
    text-indent: -9999px;
    line-height: 0;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point1 h3:after {
    content: "Earn gifts and build places";
    text-indent: 0;
    display: block;
    line-height: initial;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point1 div {
    text-indent: -9999px;
    line-height: 0;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point1 div:after {
    content: "Create anything and earn interesting boxes with thousands of virtual bricks and players.";
    text-indent: 0;
    display: block;
    line-height: initial;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point2 div {
    text-indent: -9999px;
    line-height: 0;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point2 div:after {
    content: "Visit your friend's place, chat in 3D, and be festive together.";
    text-indent: 0;
    display: block;
    line-height: initial;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point3 div {
    text-indent: -9999px;
    line-height: 0;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point3 div:after {
    content: 'Dress up as your summery or wintery character. Be careful not to get "too warm or cold".';
    text-indent: 0;
    display: block;
    line-height: initial;
}


#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point2 h3 {
    text-indent: -9999px;
    line-height: 0;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point2 h3:after {
    content: "Meet new friends, be festive";
    text-indent: 0;
    display: block;
    line-height: initial;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point3 h3 {
    text-indent: -9999px;
    line-height: 0;
}

#SplashContainer #GoodBloxAtAGlance #ThingsToDo #Point3 h3:after {
    content: "Be warm or cool";
    text-indent: 0;
    display: block;
    line-height: initial;
}

.Asset .AssetThumbnail,
.imgc {
    background: white;
}
}

@-moz-document domain("blog.goodblox.xyz") {
body {
    background: #DFDBC3 url(http://goodblox.xyz/images/body-bg.gif) top center repeat-y;
    color: #000;
}
}
