css:
@-moz-document domain("vaughn.live")
{
  div.mvn {
    width: 100%;
    height: 50px;
    position: absolute;
    z-index: 100;
}

div.mvn:hover ~ div.header {
    display: block;
}

div.header:hover {
    display: block;
}

div.header {
    display: none;
    background-color: #1a1a1a;
}

div.header_spacer {
    display: none;
}

div.menuBtnBlue {
    background-color: #272a3f;
}

div.menuBtnGreen {
    background-color: #22492f;
}

div.menuBtnGold {
    background-color: #ad2300;
}

div.height10px {
    display: none;
}

div.wrapper {
    width: 100% !important;
    max-width: 100%;
}

div.videoStatus {
    width: auto;
    position: absolute;
    z-index: 50;
    top: 40px;
    min-width: unset;
    background-color: transparent;
    border-radius: 0 8px 8px 0;
    max-width: 30%;
    height: auto;
    max-height: unset;
}

div.videoStatus:hover {
    background-color: #0000006e;
}

div.chanStatusLeft {
    visibility: hidden;
}

div.videoStatus:hover div.chanStatusLeft {
    visibility: visible;
}

div.chanStatusRight {
    visibility: hidden;
}

div.videoStatus:hover div.chanStatusRight {
    visibility: visible;
}

div.vaughnLeft {
    width: 100% !important;
    height: 100vh;
    max-width: unset !important;
}

div.videoPlayer {
    width: 100% !important;
    height: 100vh !important;
    max-width: unset;
}

div.abvsContainer {
    display: none !important;
}

div.vidPlayer {
    top: 0 !important;
}

div#MvnHlsPlayer_wrapper {
    width: 100% !important;
    height: 100vh !important;
}

div.MvnHlsPlayerBox {
    width: 100% !important;
    height: 100vh !important;
}

div#MvnHlsPlayer {
    width: 100% !important;
    height: 100vh !important;
}

video#MvnHlsPlayerLive {
    width: 100% !important;
    height: 100vh !important;
}

div.MvnAbvsLowerThirdWrapper {
    display: none;
}

div.MvnHlsPlayerBar {
    width: calc(100% - 10px) !important;
}

div.MvnHlsPlayerOverlay {
    width: 100% !important;
    height: 100vh !important;
    margin-top: 0 !important;
    top: 0;
}

.MvnHlsPlayerPlayButdivtonOverlay {
    display: none;
}

/* Chat */
div.CV3wrapper {
    width: 344px !important;
    height: 100vh !important;
    background-color: white;
}

div.CV3body {
    height: calc(100vh - 75px) !important;
}

div.theChatToggle {
    height: 100vh !important;
}

div.chatBox {
    height: 100vh !important;
}

div.chatBoxHeader {
    display: none;
}

div.abvsHeaderWrapper {
    display: none;
}

div.CV3body,
div.CV3wrapper,
textarea.CV3input {
    background-color: #1a1a1a;
    color: #ededed;
}

ul.vn_list_container li:nth-child(odd) {
    background-color: #262626;
}

div.CV3input,
div.CV3pic {
    border-top: solid 1px #222;
}

div.noticeLine {
    background-color: #222;
    border-bottom: solid 1px #1a1a1a;
}

div.chatLine {
    margin-top: 5px;
    margin-bottom: 5px;
}

span.chatLine {
    color: #ccc !important;
}

p.chatTagMod {
    background-color: #22492f;
}

span.chatUsername {
    margin-right: 5px;
    padding: 1px 1px 1px 5px;
    /*background-color: #ccc;
    border-radius: 5px;*/
    filter: invert(1);
}

a.chat_url {
    color: #6699ff;
    border-bottom: solid 1px #6699ff;
}

a.chat_url:hover {
    color: #80aaff;
    border-bottom: solid 1px #80aaff;
}

/* Bar below chat */
div.chatOptSmiley, div.chatOptHelp, div.CV3chattersListBox, div.CV3chattersListHeader, div.chatOptSettings {
    background-color: #1a1a1a;
    border-color: #000;
    box-shadow: none;
}

div.chatOptLeftContent {
    color: #fff;
}

div.chatOptSmileySel:hover {
    background-color: #666;
}

div.modalv3_status {
    background-color: #333;
    color: #fff;
    border-bottom: 0;
}

div.CV3chattersListItem {
    filter: invert(1);
}

div.CV3dosend, .mvntooltipSmall:after, [mvn-tooltip-small]:after {
    background-color: #272a3f;
    border: 1px solid #4e537e;
}

/* Browse Page */
div.catMenuContainer {
    background-color: #272a3f;
}
div.catPageContainer, div.browsePageStreamBox_lower {
    background-color: #1a1a1a;
    color: #fff;
}
div.catPageTitle {
    color: #fff;
}
.browsePageTabLine {
    display: none;
}

/* scrollbars */
* {
  scrollbar-width: thin;
  scrollbar-color: #333 #1a1a1a;
}

/* Works on Chrome/Edge/Safari */
*::-webkit-scrollbar {
  width: 12px;
}
*::-webkit-scrollbar-track {
  background: #1a1a1a;
}
*::-webkit-scrollbar-thumb {
  background-color: #ccc;
  border-radius: 20px;
  border: 3px solid #1a1a1a;
}
}
