css:
@-moz-document domain("bbs.hupu.com") {

body, .bbs-index-web{
    background: #f0f0f0;
}
.bbs-sl-web-holder{
    background: none;
}

/* top nav */
.shoutcut-floor{
    opacity: .5;
    transition: all .2s;
}
.shoutcut-floor:hover{
    opacity: 1;
}

.hp-pc-menu{
   opacity: .5;
    transition: all .2s;
}
.hp-pc-menu:hover{
   opacity: 1;
}

.hp-pc-footer{
    background: none;
   opacity: .5;
    transition: all .2s;
}
.hp-pc-footer:hover{
   opacity: 1;
}

/* left panels */
.hu-pc-navigation-wrap{
    background: none;
    opacity: .5;
    transition: all .2s;
}
.hu-pc-navigation-wrap:hover{
    opacity: 1;
}

.hu-pc-navigation-type{
    margin: 0;
    padding: 0 20px;
    background: rgba(0,0,0,.05);
    border-radius: 8px;
}
.hu-pc-navigation-type:before{
    display: none;
}

.hu-pc-navigation-topic-type-wrap:hover{
    background: #fff;
}

.hu-pc-navigation-my-focus-item>a>.logo,
.hu-pc-navigation-topic-type-title>.topic-icon{
    filter: grayscale(1);
}

/* right panels */
.bbs-index-web-right{
    opacity: .5;
    transition: all .2s;
}
.bbs-index-web-right:hover{
    opacity: 1;
}

.hot-search,
.right-post{
    background: none;
}

.hot-search .hot-search-title,
.right-post .right-post-title{
    background: rgba(0,0,0,.05);
    border-radius: 8px;
}
.hot-search .hot-search-title::before,
.right-post .right-post-title::before{
    display: none;
}

.right-post .right-post-item{
    margin: 0;
    padding: 10px 20px;
    border-bottom: 1px solid rgba(0,0,0,.05);
}
.right-post .right-post-item .post-title{
    height: auto;
    max-height:45px;
}

/* hot topic list */
.bbs-index-web-middle{
    border-radius: 8px;
    box-shadow: 0 2px 5px rgba(0,0,0,.1);
}
.bbs-index-web-middle .middle-label{
    margin-bottom: 8px;
    border-bottom: 1px solid rgba(0,0,0,.075);
}
.bbs-index-web-middle .middle-label:before{
    display: none;
}
.bbs-index-web-middle .middle-label .middle-title{
    color: #c01e2e;
}

.hot-topic-model{
    opacity: .5;
    transition: all .2s;    
}
.hot-topic-model:hover{
    opacity: 1;
}

.text-list-model .list-item-wrap .list-blank{
    height: 1px;
    margin: 12px auto;
    background: rgba(0,0,0,.075);
    border: none;
}

.text-list-model .list-item{
    padding: 14px 0;
    height: auto;
    line-height: 100%;
    border: none;
}
.text-list-model .list-item a,
.text-list-model .list-item a:hover,
.text-list-model .list-item a:visited{
    color: #000;
}
.text-list-model .list-item a:visited:not(:hover){
    color: #999;
}
.text-list-model .list-item .hot,
.text-list-model .list-item .hot:hover,
.text-list-model .list-item .hot:visited{
    color: #a33;
}
.text-list-model .list-item .hot:visited:not(:hover){
    color: #b88;
}

.text-list-model .list-item .t-info .t-lights,
.text-list-model .list-item .t-info .t-replies,
.text-list-model .list-item .t-label{
    font-size: 85%;
    color: #ccc;
    white-space: nowrap;
}

.test-img-list-model .list-item .list-item-info .list-item-desc{
    margin: 12px 0;
    min-height: auto !important;
    line-height: 150%;
}

/* board topic list */
.bbs-sl-web-topic-wrap{
    border-radius: 8px;
    box-shadow: 0 2px 5px rgba(0,0,0,.1);
}

.bbs-sl-web-intro-detail-title{
    color: #c01e2e;
}
.bbs-sl-web-intro-detail-button{
    border-radius: 11px;
}

.bbs-sl-web-type-wrap{
    margin-bottom: 12px;
}
.bbs-sl-web-post-header{
    display: none;
}

.bbs-sl-web-post-layout{
    border: none;
}

.bbs-sl-web-post-body .post-title a,
.bbs-sl-web-post-body .post-title a:hover{
    color: #000;
}
.bbs-sl-web-post-body .post-title a:visited:not(:hover){
    color: #999;
}
.bbs-sl-web-post-body .post-title .p-title{
    max-width: inherit;    
}

.bbs-sl-web-post-body .post-title .page-icon{
    white-space: nowrap;
}

.hupu-rc-pagination .hupu-rc-pagination-item,
.hupu-rc-pagination .hupu-rc-pagination-next,
.hupu-rc-pagination .hupu-rc-pagination-prev{
    padding: 4px;
    height: auto;
    min-height: auto;    
    cursor: pointer;
}
.hupu-rc-pagination .hupu-rc-pagination-item:hover,
.hupu-rc-pagination .hupu-rc-pagination-next:hover,
.hupu-rc-pagination .hupu-rc-pagination-prev:hover{
    background: #999;
    border: 1px solid #999;
}
.hupu-rc-pagination-disabled{
    opacity: .35;
    filter: grayscale(1);
}
.hupu-rc-pagination .hupu-rc-pagination-item a,
.hupu-rc-pagination .hupu-rc-pagination-next a,
.hupu-rc-pagination .hupu-rc-pagination-prev a{
    font-size: 100%;
}

/* post view */
.post-operate-bar-wrapper .post-operate-bar-top .list-icon{
    position: relative;
    margin: 0 0 10px 0;
}
.post-operate-bar-wrapper .post-operate-bar-top .list-icon-detail,
.post-operate-bar-wrapper .post-operate-bar-share .list-icon-share{
    background: none;
    border: 1px solid rgba(0,0,0,.1);
}

.post-operate-bar-wrapper .post-operate-bar-top .list-icon-detail:hover,
.post-operate-bar-wrapper .post-operate-bar-share .list-icon-share:hover{
    background: #fff;
}
.post-operate-bar-wrapper .post-operate-bar-top .list-icon-name{
    display: none;
    position: absolute;
    right: 100%;
    margin: 0 8px 0 0;
    padding: 8px 0;
    white-space: nowrap;
}
.post-operate-bar-wrapper .post-operate-bar-top .list-icon:hover .list-icon-name{
    display: block;
}

.post-operate-bar-wrapper .post-operate-bar-share{
    margin: 24px 0 0 0;
}
.post-operate-bar-wrapper .post-operate-bar-share .list-icon-share{
    background: none;
    border: 1px solid rgba(0,0,0,.1);
}

.bbs-post-web-body-right-wrapper .game-center-entrance-container-title{
    margin: 0 0 4px 0;
    background:   rgba(0,0,0,.05);
    border-radius: 8px;
    opacity: .5;
    transition: all .2s;
}
.bbs-post-web-body-right-wrapper .game-center-entrance-container-title:hover{
    opacity: 1;
}
.bbs-post-web-body-right-wrapper .game-center-entrance-container-title:before{
    display: none;
}
.game-center-sidebar,
#game-center-entrance-container{
    border-radius: 8px;
    opacity: .5;
    transition: all .2s;
}
.game-center-sidebar:hover,
#game-center-entrance-container:hover{
    opacity: 1;
}

.right-post,
.bbs-post-web .download-app{
    opacity: .5;
    transition: all .2s;
}
.right-post:hover,
.bbs-post-web .download-app:hover{
    opacity: 1;
}


.bbs-post-web-body-left-wrapper{
    border-radius: 8px;
    box-shadow: 0 2px 5px rgba(0,0,0,.1);
}

.post-fix-title-config .fixed-btn{
    border-radius: 16px;
}

.bbs-post-web-main .post-wrapper{
    padding: 0;
    background: none;
}
.bbs-post-content .main-post-info{
    margin-bottom: 36px;    
}

.post-user-comp-info-top-name,
.post-reply-list .reply-list-content .post-reply-list-user-info-top-name,
.reply-detail-list .reply-list-content .reply-detail-list-user-info-top-name{
    font-weight: bold;
    color: #000;
}

.post-user-comp-info-top-tip,
.post-reply-list .reply-list-content .post-reply-list-user-info-top-tip{
    color: #fff;
    background:#f60;
}

.bbs-thread-comp .thread-content-detail .img-wrapper-embedded img{
    border: 1px solid rgba(0,0,0,.1)
}

.bbs-post-wrapper-title:before{
    display: none;
}
.bbs-post-wrapper.light .bbs-post-wrapper-title,
.bbs-post-wrapper.gray .bbs-post-wrapper-title{
    padding: 36px 28px;
    border: none;
}

.post-reply-list.show-detail .todo-list-replay:after{
    z-index: 999;
}
.bbs-post-wrapper.gray .reply-detail-wrapper,
.bbs-post-wrapper.light .reply-detail-wrapper{
    padding: 18px;
    border-radius: 8px;
}
.bbs-post-wrapper.gray .reply-detail-wrapper{
    box-shadow: 0 1px 3px rgba(0,0,0,.15);
}

.reply-detail-wrapper .reply-hide .reply-detail-hide{
    padding: 4px 12px;
    color: #666;
    background: none;
    border: 1px solid rgba(0,0,0,.2);
    border-radius: 18px;   
}
.reply-detail-wrapper .reply-hide .reply-detail-hide:hover{
    background: rgba(0,0,0,.05);
}

.bbs-post-web-main-title{
    margin: 16px 0;    
}

.bbs-post-wrapper.light{
    margin: -8px;
    padding: 0 12px;
    background: #f9f8f2;
    border: 1px solid #efe7c2;
    border-radius: 8px;
}
.bbs-post-wrapper.gray{
    background: none;
}

.bbs-post-wrapper .post-reply-list:after,
.bbs-post-wrapper .reply-detail-list:after{
    display: none;
}

.post-reply-list,
.reply-detail-list{
    padding: 16px 8px;
    border-top: 1px solid rgba(0,0,0,.05);
}
.reply-detail-list:first-child{
    border: none;
}

.post-user-comp-info,
.post-reply-list .reply-list-content .post-reply-list-user-info-top{
    align-items: flex-start;
}

.post-user-comp-info-top-time,
.post-reply-list .reply-list-content .post-reply-list-user-info-top-time{
    font-size: 12px;
}

.post-user-comp{
    margin-bottom: -36px;
}
.post-user-comp-info-bottom{
    display: none;
}

.post-reply-list .bbs-thread-comp{
    margin: 12px 0;
}

.bbs-thread-comp-container,
.bbs-post-wrapper.light .quote-thread .bbs-thread-comp-container{
    background: rgba(0,0,0,.07);
    border-radius: 8px;
    opacity: .7;
    transition: all .2s;
}
.bbs-thread-comp-container:hover,
.bbs-post-wrapper.light .quote-thread .bbs-thread-comp-container:hover{
    opacity: 1;
}
.bbs-thread-comp a{
    color: #039;
}
.bbs-thread-comp-container .toggle-thread{
    color: #666;
    text-decoration: underline;
}

.post-reply-list .reply-list-content .post-reply-list-operate,
.reply-detail-list .reply-list-content .reply-detail-list-operate{
    margin: 0 0 0 -10px;
}
.post-reply-list .reply-list-content .post-reply-list-operate .todo-list .todo-list-text.bold,
.reply-detail-list .reply-list-content .reply-detail-list-operate .todo-list .todo-list-text.bold{
    font-weight: normal;
}

.bbs-post-web .pagination{
    margin: 24px 0;
    padding: 0;
}
}
