css:
@-moz-document url-prefix("https://www.eldarya.pl/marketplace"), url-prefix("https://www.eldarya.fr/marketplace"), url-prefix("https://www.eldarya.com/marketplace"), url-prefix("https://www.eldarya.de/marketplace"), url-prefix("https://www.eldarya.br/marketplace"), url-prefix("https://www.eldarya.es/marketplace"), url-prefix("https://www.eldarya.hu/marketplace"), url-prefix("https://www.eldarya.it/marketplace"), url-prefix("https://www.eldarya.ru/marketplace") {
.price-item
{
    font-size: 23px;
    margin: 7px;
}

.maana-icon
{
    display: none;
}

.abstract-currentPrice
{
   font-size: 11px;
   text-transform: none;
    
}

.marketplace-abstract .abstract-content
{
    box-shadow: none;
    
}

.marketplace-abstract .abstract-icon

{
    background: none;
    box-shadow: none;
    top: 0;
}

.marketplace-search-items li, .marketplace-itemsForSale-items li, .marketplace-auctions-item, .marketplace-sales-item
{
    height: 280px;
    border-radius: 20px;
}

.marketplace-abstract .abstract-container 

{
    top: 5px;
}

#marketplace-abstract-sales .abstract-actions, #marketplace-abstract-purchases .abstract-actions

{
    top: 0;
}

.marketplace-itemsForSale-items li.marketplace-itemsForSale-item

{
    height: 170px;
}
}
