css:
@-moz-document url-prefix("https://app.crisp.chat") {
:root {
    --text-main: #8dabc4;
    --text-light: #a8c6df;
  --text-active: #1e90ff;
  --text-second: #ffffff;
  --note-back: #e5b251;
  --main-dark: #15202b;
    --chat-back: #2e3e49;
    
    --tab-head: #373e51;
    --svg-icon: #4472c4;
    --rsidebar-text-border: #4b4d57;
}

/* convo */
.c-conversation-box {
    background-color: var(--chat-back);
    color: white;
    border: none;
}
.c-conversation-box-live-translate {
    background-color: var(--chat-back);
    
}
.c-conversation-box-field {
     background-color: var(--chat-back);
}
.js-textarea {
     color: white !important;
}
.c-conversation-tabs--padded {
    border: none !important;
}
.c-conversation-box__field {
    border: none !important;
}
.c-conversation-tabs .c-conversation-tabs__item .c-conversation-tabs__header {
     color: var(--text-main);
}
.c-conversation-tabs .c-conversation-tabs__item--active .c-conversation-tabs__header {
     color: #0e79c4;
}

/* convo messages */
.c-conversation-box-content-message-bubble--user.c-conversation-box-content-message-bubble--text .c-conversation-box-content-message-bubble__wrapper {
    background-color: #4f5d7f;
    color: white;
    font-size: 12pt;
}
.c-conversation-box-content-message-bubble--note .c-conversation-box-content-message-bubble__wrapper {
    background-color: var(--note-back);
}
.c-conversation-box-content-message-bubble__note-header2 {
    display: none !important;
}
.c-conversation-box-content-message-bubble__note-header {
  visibility: hidden;
}

.c-conversation-box-content-message-bubble__note-header:before {
  content: 'Note' !important;
    display: block !important;
  visibility: visible;
  position: absolute;
}
.c-conversation-box-content-message-bubble--note .c-conversation-box-content-message-bubble__content {
    margin-left: 5px;
}

.c-conversation-box-content-message-bubble--operator.c-conversation-box-content-message-bubble--text .c-conversation-box-content-message-bubble__wrapper {
    background-color: #292b2f;
}
.c-conversation-box-content__message--is-last .c-conversation-box-content-message-bubble--operator.c-conversation-box-content-message-bubble--text .c-conversation-box-content-message-bubble__wrapper {
    background-color: #0c85e0;
}


.c-conversation-box-content-message-bubble--event .c-conversation-box-content-message-bubble__wrapper {
    background-color: #34bb1f;
}
.c-conversation-box-content-message__label {
    color: var(--text-light);
}


/* convo chat bar */
.c-conversation-box-suggestions__header {
    border-color: #4b4d57 !important;
    background-color: #393c42 !important;
}
.c-conversation-box-suggestions__title {
    color: var(--text-light);
}
.c-conversation-box-suggestions__header .c-base-commands__text {
    color: var(--text-light) !important;
}

.c-conversation-box-suggestions__body, .c-conversation-box-suggestion-tag {
     background-color: #36393f !important;
}
.c-conversation-box-suggestion-shortcut {
    margin-left: 20px;
    margin-right: 20px;
    margin-bottom: 5px;
    background-color: #292b2f;
}


/* convo heading */

.c-conversation-box-state--resolved .c-conversation-box-state__button {
    background-color: #2ea44f !important;
}

.c-conversation-box-state--unresolved .c-conversation-box-state__button {
    background-color: #d73a49 !important;
}
.c-conversation-box-live-translate__nickname {
    color: var(--text-light);
}
.c-conversation-box-live-translate__selector {
    color: var(--text-main) !important;
}
.c-conversation-box-live-translate__operator {
    color: var(--text-main) !important;
}


.c-conversation-box__live-translate {
    border-color: var(--chat-back) !important;
}

/* right sidebar */
.c-conversation-profile-card-action, .c-base-pages__page, .c-conversation-profile__header, .c-conversation-profile-card--padding .c-conversation-profile-card__body {
    background-color: #292e3d !important;
}
.c-conversation-profile-card-action:hover, .c-base-pages__page:hover {
    background-color: #24292e !important;
}
.c-base-pages__page {
    border-color: #4b4d57 !important;
}
.c-conversation-profile-card-action {
    border-color: #4b4d57 !important;
}
.c-conversation-profile__card {
    border-color: #25374f !important;
}
.c-conversation-profile {
    background-color: #292e3d;
    
}
.c-conversation-profile__buttons2 {
    display: none !important;
}
/* Remove annoying white backgrounds */
.c-conversation-profile-card-main .c-conversation-profile-card-item__content::after, .c-base-upgrade__slot .u-ellipsis::after, .c-conversation-profile-card-main .u-ellipsis::after, .c-conversation-profile-card-devices .c-conversation-profile__item.u-has-tooltip .u-ellipsis::after, .c-conversation-profile__nickname::after, .c-conversation-profile__email::after, .c-conversation-profile__location::after {
    background: none !important;
}

.c-conversation-profile-card__header {
    background-color: var(--tab-head) !important;
    border-color: #25374f !important;
}
.c-conversation-profile__footer {
    border-color: #25374f !important;
}
.c-conversation-profile-card--padding {
    border-color: #25374f !important;
}
.c-conversation-profile__header {
    border-color: var(--chat-back) !important;
}
.c-conversation-profile-card-routing__name {
    color: white;
}
.c-conversation-profile__nickname {
    color: white;
}
.c-conversation-profile-card-action__label, 
.c-conversation-profile-card-item__label, 
.c-conversation-profile__email,
.c-conversation-profile-card-main .c-conversation-profile-card-item__label {
    color: var(--text-main) !important;
}
.c-conversation-profile__item svg {
    fill: var(--svg-icon) !important;
}


.c-conversation-profile-card-item__link {
     color: var(--text-main) !important;
}
.c-conversation-profile__email--not-set {
    visibility: hidden;
}


.c-base-pages__title {
    color: var(--text-main) !important;
}

.js-input-value {
     background-color: var(--chat-back);
}
.c-profile-card-segments__input {
    border-color: var(--sidebar-text) !important;
   
}
.c-conversation-profile-card-data-item {
     border-color: var(--rsidebar-text-border) !important;
}
.c-conversation-profile-card-data-item__cell {
    border-color: var(--rsidebar-text-border) !important;
    background-color: var(--chat-back) !important;
}
.c-conversation-profile-card-data-item__input {
    background-color: var(--chat-back) !important;
}

.c-conversation-profile-card-data-item__input, .js-input-value {
    color: white !important;
}
.c-conversation-profile-card-data-item__input::placeholder, .js-input-value::placeholder {
    color: var(--text-main) !important;
}



/* left sidebar */
.c-conversation-menu-item, .c-conversation-tabs {
    background-color: #1c293b;
    border: none;;
}
.c-conversation-menu-item--active {
    background-color: #25374f !important;
}

.c-conversation-menu__body {
    background-color: #25374f;
}

.c-conversation-menu-footer__actions, .c-conversation-menu-footer__operators, .c-conversation-menu-footer__routing {
    background-color: #15202b !important;
}
.c-conversation-menu__footer {
    border: none !important;
}

.c-conversation-menu-item__nickname {
    color: rgb(200,200,200) !important;
}
.c-conversation-menu-item__last-message {
    color: var(--text-main) !important;
}
.c-conversation-menu-item:hover {
    background-color: #2c4360 !important;
}
.c-conversation-tabs--center {
    border: none !important;
}

.c-conversation-menu__body {
    border: none !important;
}

/* main left sidebar */
.c-sidebar {
    background-color: var(--main-dark);
}

/* Inbox loading */
.c-inbox-index__content, .o-layout {
    background-color: var(--chat-back) !important;
    
}

/* Inbox tabs other */
.c-conversation-menu__information {
    background-color: #25374f !important;
    
}
.c-base-empty-spot__title {
    color: var(--text-main) !important;
}

/* Convo banner */
.c-conversation-box-content__banner {
    background-color: #4472c4;
}
}
