css:
@-moz-document domain(techmeme.com) {

body,
#sponsorposts,
#countercol,
.ifsp,
.podcast,
.sbti,
#events {
    background: #050a0e;
}

h2,
#events a,
#events .efoot A {
    color: #bbb;
}

.ed STRONG A:link,
.ii,
.cosp > a > span,
.cosp A:link,
.podcast {
    color: #b1afaf;
}

.ed STRONG A:hover,
#events .efoot A:hover {
    background: #132432;
}

.dxd {
    background-color: #050a0e;
}

#events .featured .rhov {
    background: #1d2b35;
}

#events .featured .rhov:hover,
#events .rhov:hover {
    background: #132432;
}

.ed STRONG A:visited {
    color: #746255;
}

.topline,
#0i1.item {
    border-bottom: solid 1px #1e303f;
}
}
