css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cs-manager.com") {

body {	background: #FFEEEE !important;  }

a {
	color: #606060 !important;
}

#header {
	background-color: #FF7A7A !important;
	background-image: url("http://www.sp-in.dk/csm/theme/pink/logo_pink.png") !important;
	background-repeat: no-repeat !important;
	border-right: 1px solid #DDBBBB !important;
	border-bottom: 1px solid #DDBBBB !important;
	border-left: none;
	border-top: none;
}

#header img {	visibility: hidden !important;} 
#header a img {	visibility: hidden !important;} 

#container, #footer_container,.post,#content {
	color: #000000 !important;
	background: #FFDDDD !important;
	border-right: 1px solid #DDBBBB !important;
	border-bottom: none; 
	border-left: none;
	border-top: none;
}

table.rank,table.login {
	border: 1px solid #DDBBBB !important; 
}

table.rank,table.login,table.mail,table.info {
	background: #FFBBBB !important;
}

td.space,div.space {
	background: #FFDDDD !important;
	border:none !important;
}
a.player,td.player,td.top,td.tactic,a.tactic {
	background: #FFCCCC !important;
	color: #000000 !important;
}
a.tactic:hover {
	background: #EE8888 !important;
}
td.position {
	background: #FFBBBB !important;
}


td.head, h2, h3 {
	color: #000000 !important;
	background-color: #FF7A7A !important;
	background-image: none !important;
	border-top: 2px solid #DD3333 !important;
	border-left: 2px solid #DD3333 !important;
	border-right: 2px solid #EEAAAA !important;
	border-bottom: 2px solid #EEAAAA !important;
}


li a {
	background: #FFBBBB !important;
	color: #000000 !important;
	font-style: normal !important;
}


ul#menu li a {
	border-bottom: 1px solid #DDBBBB !important;
	border-right: none !important;
	border-left: none !important;
	border-top: none !important;
}


ul#tab li a {
	border: 1px solid DDBBBB !important;
}

li a:hover {
	background: #EE8888 !important;
	font-style: normal !important;
}


ul#tab li span {
	background: #FFDDDD !important;
	color: #000000 !important;
	font-style: normal !important;
	border-bottom: #FFDDDD !important;
}


li span {
	color: #000000 !important;
}


table.mail tr.head td, table.mail td.select, table.mail td.delete {
	background-color: #EE8888 !important;
}


#menu, #footer_left, #footer_right, div#container>div#info {
	background: #BB9999 !important;
}


form#search {
	border-bottom: 1px solid #DDBBBB !important;
	background: #FFBBBB !important;
}


input,select,textarea,radio {
	background: #FFEEEE !important;
	border: 1px solid #DDBBBB !important;
}
.buttonsmall, .button {
	background: #FFEEEE !important;
	color: #000000 !important;
	border-top: 2px solid #DD3333 !important;
	border-left: 2px solid #DD3333 !important;
	border-right: 2px solid #EEAAAA !important;
	border-bottom: 2px solid #EEAAAA !important;
	font-size: 10px !important;
	padding: 0px !important;
}


td.value {
	background: #FFCCCC !important;
}
td.foot,td.info {
	background: #FFBBBB !important;
}


#training_map,#training_player,#skill,#trainer,#training_players {
	background: #FFBBBB !important;
}
div#training_type {
	background: #EE8888 !important;
}


tr.even {
	background: #FFE3E3 !important;
}
tr.odd {
	background: #FFEEEE !important;
}
div.modify {
	background: #FFEEEE !important;
}

}
