css:
@-moz-document domain(flomo.app),domain(flomoapp.com) {
  html, body{
    background:#F1FAFA !important;
  }
  
  div.el-loading-mask{
    background-color:#F1FAFA !important;
  }

  div.memos{
    overflow:visible !important;
  }

  div.input-box{
    border-width:0px  !important;
  }
  div.input{
    padding:0px !important;
    margin:10px 20px 10px !important;
  }

  div.card{
    margin:3px 0 !important;
  }

  div.input,div.memo div.card{
    border-radius:6px !important;
    overflow:hidden !important;
    transition:box-shadow 0.4s ease-in-out  !important;
    box-shadow:0px 2px 8px 6px rgba(0,0,0,0.02)  !important;
  }

  div.input:hover,div.memo div.card:hover{
    box-shadow:0px 2px 13px 6px rgba(0,0,0,0.1)  !important;
  }
  main.el-main::-webkit-scrollbar {
      width: 0 !important;
  }
}
