css:
@-moz-document domain("free-mp3-download.net") {
/* Background */
.blue.darken-1 {
    background-color: #1c1c1c !important;
}

/* Card */
.blue.darken-2 {
    background-color: #0000 !important;
}

/* footer */
.blue.darken-3 {
    background-color: #222 !important;
}

/* -------------- Main page -------------- */
.container {
    width: 95%;
    max-width: 1400px;
}

#results > table {
    border-collapse: inherit;
}

table.highlight > thead > tr > th {
    text-align: center;
    font-size: 16px;
}

table.highlight > tbody > tr {
    transition: all 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}

table.highlight > tbody > tr:hover {
    background-color: #2a364c;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

table.highlight > tbody > tr > td:nth-child(2) {
    text-align: center;
}

table.highlight > tbody > tr > td:nth-child(3) {
    text-align: center;
}

table.highlight > tbody > tr > td {
    padding: 15px 15px
}

.circle {
    border-radius: 10%;
}

/* -------------- Download page -------------- */
.row .col.offset-l4 {
    width: 100%;
    margin: 0px;
}

.card {
    display: grid;
    box-shadow: 0px 0px #0000;
}

.card .card-image {
    grid-area: 1 / 2 / span 2 / span 2;
    margin: auto;
}

.card .card-image img {
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
}

.card .card-content {

    padding: 32px;
    margin-right: 24px;
    background: #262626;
}

/* title */
.card .card-content p:nth-child(1) {
    text-align: center;
    font-size: 24px;
    font-weight: bold;
}

/* title */
.card .card-content p:nth-child(5) {
    margin-bottom: 10px
}

.card .card-action {
    padding: 32px;
    margin-right: 24px;
    border: none;
    background: #262626;
}

/* Hide "Whatsapp" */
.card a {
    position: absolute;
    transform: scale(0, 0);
}

/* Hide "Back to search" */
.card > p {
    position: absolute;
    transform: scale(0, 0);
}

/* -------------- About -------------- */
.modal {
    background-color: #222 !important;
    color: white;
}

.modal .modal-footer {
    background-color: #222 !important;
}

.modal .modal-close {
    color: white;
}

/* -------------- Buttons -------------- */
.blue.darken-4 {
    background-color: #0000 !important;
}
.grey.lighten-5 {
    background-color: #0000 !important;
    color: white !important;

    border-color: #858585;
}

.btn {
    border: #2a3f90 10px;
    border-style: solid;
    border-width: 4px;
    border-radius: 0px;

    padding: 33px;
    padding-top: 0px;

    margin: 3px;

    box-shadow: none;

    background: #0000 !important;

    transition: all 0.4s cubic-bezier(0.16, 1, 0.3, 1);
}

.btn:hover {
    padding: calc(33px + 3px);
    padding-top: 3px;

    margin: 0px;

    box-shadow: none;

    background: #0002 !important;
}
}
