css:
@document regexp("https://(mail|calendar)\.google\.com/.*") {
html { filter: invert(.9); }
    img, [style*=background-image] { filter: invert(.9); }
    .J-M, .tO, .SK { /* popups */
        box-shadow: 0 0 0 2px #eee;
    }

    .nH.oy8Mbf.nn.aeN {
        border-right: 1px solid rgba(0,0,0,.05);
    }

    .Nt {
        border-right: 0 !important;
    }

    ::-webkit-scrollbar {
        width: 1px !important;
    }
}
